    html.room #file_list .file.uploading, html.room #file_list.uneven .file.uploading:nth-child(2n+1), html.room #file_list.even .file.uploading:nth-child(2n) { background: #323232 !important; }

    textarea.defaultValue, input[type=text].defaultValue, input[type=password].defaultValue, body, html.room #header #room_search #clearsearch, a, a:visited { color:#7D7D7D!important }
table th {
