/* fonts */

html,body {
	font-family: consolas, 'haxrcorp', consolas, lucida console, lucida sans typewriter, monaco !important; 
	font-weight: 100 !important; font-size: 12px !important;}

b,strong {
	font-family: monospace, lucida console, lucida sans typewriter, monaco !important; 
	font-weight: 200 !important; }
h1,h2,h3,h4,h5,h6 {
	font-family: 'lain',monospace, lucida console, lucida sans typewriter, monaco !important; 
	font-weight: 400; }
@font-face {
  font-family: 'lain';
  src: url('https://lainchan.org/stylesheets/fonts/nrdyyh.woff') format('woff'),
       url('https://lainchan.org/stylesheets/fonts/fonts/tojcxo.TTF') format('truetype');
}
 

	/*input font size*/
html.room #chat_frame #chat_input { font-size: 11px !important; }
	html.room #chat_frame #chat_messages_frame #chat_messages .message { font-size: 11px !important;  }


    html, html.room #header, div.ui_frame { background: #323232 !important; }

