    html.room #file_list.even .file:nth-child(2n+1) { background: #323232 !important; }

    html.room #chat_frame #chat_messages_frame #chat_messages .message.user { background: #323232 !important; }

    
		html.room #header #room_name_container h1 {
			font-size: 15px !important;
margin: 14px 0px 0px 0px!important; }

