css:
@-moz-document url-prefix(https://www.tumblr.com/) {
/*.new_post .new_post_label_icon {
  background-image: none !important;
}*/
.new_post .new_post_label > I {display: none}

.new_post .new_post_label {
  font-family:'Calibri', 'Trebuchet MS', sans-serif !important;
  font-size: 12px !important;
  text-transform: uppercase;
  color: #000000 !important;
  padding: 74px 1px 15px 0 !important;
  transition: padding .2s linear;
}
.new_post .new_post_label:hover {
  padding: 67px 1px 22px 0 !important;
}

.post.new_post {
  background: #ffffff url('https://40.media.tumblr.com/fcaa12ed4791dbd7597e95d3b2f2d1db/tumblr_nnqzn6j13K1sduc37o1_540.png') center no-repeat !important;
}

.new_post .post_avatar:after {
  width: 0 !important; height: 0 !important;
  border-top: 8px solid transparent;
  border-bottom: 8px solid transparent;
  border-right: 9px solid #000000;
  margin-left: 12px !important;
background: none !important;
}
}
