css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.firefox-browser.de/forum) 

{
 
body {
     background-color:#FAFAD2 !important;
          }

 span.maintitle + br + span.gen { 
     display: none !important; 
          } 

 span.maintitle + br + span.gen + table { 
     margin-top: 8px !important; 
          }

 .bodyline { 
     background-color: 	#FAFAD2 !important; border: 0px #212121 solid!important; 
          }

 .forumline { 
     background-color: 	#DCDCDC !important; border: 0px #212121 solid!important; 
          }
 
 img[src="templates/subSilver/images/logo_phpBB.jpg"] {
     max-height: 0em !important;
          }

 td.row1, .row1, .row3, .row3Right  { 
     background-color: 	#FFFFF0 !important; 
          }

 td.row2 { 
     background-color: 	#F5F5F5 !important; 
          }

     .maintitle {
     font-family:times new roman !important;
     font-size: 17pt !important;
     font-weight: normal !important;
     color: #000000 !important;     
          }

     span.maintitle + br + span.gen { 
     font-family:times new roman !important;
     font-size: 11pt !important;
     font-weight: normal !important; 
          }
 
 .mainmenu { 
     font-family:times new roman !important;
     font-size: 10pt !important;
     font-weight: normal !important;
     color: #000000 !important;     
          }

 .gensmall {
     font-size: 7.5pt !important;
     color: #000000 !important; 
          }

 .nav { 
     font-family:times new roman !important;
     font-size: 10.5pt !important;
     font-weight: bold !important;
     color: #000000 !important;     
          }
 
 .thCornerL, .thTop, .thCornerR, .thLeft, .thRight, .thHead {
     border: 0px #212121 solid!important; 
     color: #FFFFFF !important;         
     font-family:times new roman !important;   
     font-size: 10.2pt !important;  
          }

 .cattitle {
     color: #696969 !important;         
     font-family:times new roman !important;   
     font-size: 10.2pt !important;  
          }

 .catLeft, .catHead {
     border: 0px #212121 solid!important;
          }
 
 .row1, .row2 { 
     font-family:tahoma !important;
          }

 .forumlink, .topictitle, .postdetails {
     color: #696969 !important; 
          }

 .copyright {     
     display: none !important;
          }

}
