css:
@-moz-document regexp("https://[a-z]+.google.com/.*") {
/* status, all + 100px */
#gbsfw,
#gbsfw iframe,
#gbwc,
#gbwc iframe {
  width: 500px !important;
  min-width: 500px !important;
}

}

@-moz-document domain("plus.google.com") {
body {
  font-size: 16px;
}

.gA, /* comments */
.Bt.Pm, .e9c /* posts */ {
  max-height: none !important;
}

.YR, /* comment 'show more' */
.d-s.gj {
  display: none !important;
}

.lC {
  max-height: none !important;
}

/* posts */
.dn {
  font-size: initial !important;
}

/*comments */
.Xa, .Aq,
.Ry, .df, /* textarea */
.l4a /* popup comments */ {
  font-size: 14px !important;
}

.Ic {
  width: 700px; /* main column width */
}

.ona.Fdb.csa,
.Ak.Kic,
.tna.Uma {
  -width: auto !important; /* reset width */
}

/* status, all + 100px */
.l4a,
.tQb,
.gb,
.Kac,
.f4a,
.Nac,
.MNn0h {
  width: 460px !important;
}
.vQb {
  width: 364px !important;
}
.fac,
.Y9b,
.Jub,
.Hub,
.Gub {
  width: 340px !important;
}


}
