css:
@-moz-document domain(wikipedia.org) {

/* hide tagline */
div#siteSub {display:none;}

#mw-navigation {display:none;}
body {
    font-size: inherit;
    background: #EDEDED url(http://i.imgur.com/5L9gs4g.png) center center scroll;
}
#bodyContent {
    font: 14px/1.5 Open Sans, sans-serif;
    color: #333;
}
body b {
    font-weight: 600 !important;
}
p {
    margin: 0 0 1em 0;
    line-height: inherit;
    color: #555;
}

/* title */
.firstHeading {
font: 48px/1 AquilineTwo, IM Fell Great Primer Pro, Georgia, Open Sans !important;
color: #333 !important;
/* font-weight: 400 !important; */
    display: block;
    margin: 0 !important;
padding: 0px 0 38px 10px !important;
position: relative  !important;
z-index: -1 !important;
}
/* redirect title */
div#contentSub {display:none !important;}

.dablink, .rellink, .relarticle {
    font-size: 0.95em;
    color: #777;
    position: relative;
    z-index: 99;
}



#column-one, #footer { display: none !important; } 

/* page width and properties */
div#content.mw-body { 
/*width: 75% !important;*/
    max-width: 720px;
margin: 15px auto 25px auto !important;  /* page width */
/* border: 5px solid #1E1E1E !important; */
border: none !important;
border-radius: 2px !important;
/* box-shadow: 0 5px 35px 4px rgba(0,0,0,0.7); SLOW */
padding: 0px 1.6em 0px 1.6em !important;
    background: none;
}

/* hide left and top panel */
#mw-panel {display:none!important;}
.noprint {display:none!important;}

#panel, #head,#page-base
  { display: none !important; } 
  
  
  

/* section headers */
h1, h2, h3, h4, h5, h6 {
    border: none !important;
    font-family: Open Sans, Droid Serif, PT Serif, Georgia, Helvetica, sans-serif;
    margin: 0.5em 0;
    color: #333;
}
h2 {
    font-size: 21px;
}
h3 {
    font-size: 18px;
    font-weight: 400;
}
h4 {
    font-size: 16px;
    font-weight: 400;
}
h5 {
    font-size: 1em;
    font-weight: 400;
} 
  
  
  
  
  
  
  
  

/* pictures / images */
.infobox {
    background-color: #FCFCFC !important;
    border:1px solid #DDD !important;
    border-radius: 2px !important;
/*    box-shadow: 0px 0px 2px 0 #999 !important;  */
    }

/*
.infobox:hover {
    background-color: #F1F1F1 !important; 
    box-shadow: 0 0 3px #454545 !important;
    -moz-transition: 400ms
    }
*/



/*.thumb:hover {
    background-color: #F1F1F1 !important; 
    box-shadow: 0 0 3px #454545 !important;
    -moz-transition: 400ms
    } */

/*div.thumb.tright {margin:16px 24px!important;}*/

.thumbinner {
    padding: 4px !important;
    border: none !important;
    box-shadow: 0px 0px 2px 0 #CCC !important;
    border-radius: 3px !important;
    }

#toc, .toc {
    font-family: Segoe UI !important;
    border: 1px solid #DDD !important;
/*    box-shadow: 0px 0px 2px 0 #777 !important; */
    padding: 6px 12px !important;
    border-radius: 3px !important;
    font-size: .9em;
    font-weight: 200;
    }
.toc a {
    display: block;
    margin: 0.3em 0;
}
.toclevel-1 > a > .toctext {
    font-weight: 400;
}
.toc h2 {display:none !important;}
/*.toc:hover {
    background-color: #F5F5F5 !important; 
    box-shadow: 0 0 4px #666 !important;
    -moz-transition: 400ms;
    }*/
.toccolours, .thumb {
    border: 1px solid #EEE !important;
/*    box-shadow: 0px 0px 3px 0 #999 !important; */
    border-radius: 2px !important;
    }
div.toctitle {text-decoration: none !important;}
span.toctoggle {display:none !important;}


span.editsection, span.mw-editsection {display: none !important;}

a:link {color: #274787 !important;}
a:active {color:#36c !important;}
/*a:visited {color: #888 !important;} */
a:hover {color: #36c !important; text-decoration:underline!important;} 




/* search results */
.mw-search-result-data, .mw-specialpage-summary, .mw-search-formheader, .mw-search-createlink {display:none !important;}
.mw-search-results {padding-top: 15px !important;}
div.mw-jump {display:none!important;}
table#mw-search-top-table tr td input {display:none!important;}

/* hide citations */
sup[class="reference"], span[id="References"], ol[class="references"], .refbegin {display:none !important;}

/* hide rating */
div#mw-articlefeedback {display:none!important;}

/* hide coordinates on location pages */
#coordinates {display:none !important;}


/* hide hide button
.collapseButton {display:none !important;}  */
    
/* hide protected lock */
div#protected-icon {display:none !important;}

/* merge suggestion */
table[class="metadata plainlinks ambox ambox-move"] {display:none !important;}

/* hide stubs */
TABLE.metadata.plainlinks.stub {display:none!important;}

/* V•T•E */
.plainlinks.hlist.navbar.mini {
    display: none;
}

/* Categories */
#mw-normal-catlinks {
    font-size: 0.9em;
}


}



@-moz-document url-prefix(http://en.wikipedia.org/wiki/File:) {

/* hide image properties box on image page */
ul#filetoc {display:none!important;}
h1#firstHeading.firstHeading {display:none!important;}
div.sharedUploadNotice {display:none!important;}
div#file.fullImageLink {text-align:center!important;}
}
