css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("book.zongheng.com"), domain("tome.zongheng.com") {
    .readtext {
        -moz-user-select: all !important;
    }
    /* compress line spacing */
    .read_con .content .readtext p {
        line-height: normal;
        padding: 0;
    }
    /* auto fit to window width */
    #uiViewPanel {
        width: auto !important;
    }
    /* auto fit directory offset */
    .chap_list {
        left: 20px;
    }
    #uiDirectoryButton {
        margin-left: 0;
    }
    #ui_chapter_comment_panel {
        display: none !important;
    }
    /* auto fit shortcuts offset */
    .quick_fixed {
        right: 4px;
        left: auto;
        margin-left: 0;
    }
    #uiShortcutButton {
        margin-left: 0;
    }
    /* ADs hider */
    body.read_on .wrap:not(#topnavbar), 
    body.read_on .cl0, 
    body.read_on .cl5, 
    body.read_on .cl10, 
    body.read_on .cl15, 
    body.read_on .cl20, 
    body.read_on .line {
        display: none;
    }
}
