@font-face {
  font-family: 'Fauna One';
  font-style: normal;
  font-weight: 400;
  src: local('Fauna One'), local('FaunaOne-Regular'), url(http://themes.googleusercontent.com/static/fonts/faunaone/v2/cSd7NBXNFQWK4oX1706dYz8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}


/*[[topbar]]*/
/*[[sidebar]]*/

	  
div#right_column,
div#right_column:after {background: none !important;}	 
	 
body { background-image:url('http://dl.dropbox.com/s/8ocx9yyl6edlqq3/1920x1080.jpg') !important;
background-color:#fff !important;
background-repeat:repeat !important;
background-position: center top !important;
background-attachment:fixed !important;
color: #5d3f39 !important;
opacity: 1 !important;
font-size: 11px !important;
font-family: 'Fauna One' !important;
text-align: justify !important;}

h1, h2, #footer {color: #bbb !important; font-family: 'Trebuchet MS' !important;}

img {-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}
    
img:hover {opacity:0.6 !important; -webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}   

a:link, a:active, a:visited {color: #be640f !important; text-decoration: none !important;
-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;} 
a:hover {color: #df9627 !important; text-decoration: none !important;
-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;} 


.post_info a:link, .post_info a:active, .post_info a:visited {color: #df9627 !important;
-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear; text-decoration: none !important;} 
.post_info a:hover {color: #be640f !important; text-decoration: none !important;
-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;} 

.post_content {background:transparent !important;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content { background: #53230f !important;  }
.post_info {color: #5c352c !important;
  font-size: 11px !important;}
.post_content {font-size: 11px !important;}



/* couleur des notifications (+100) */
#new_post_notice_container {background: #df9627 !important;}

/* barre de recherche en haut à droite */
.search_form_field, .search_form_row {
background: #be640f !important; border-radius: 20px !important;}

input {font-family: 'Old Standard TT' !important; text-transform: none;}

 

/* Posts questions */
.asker, .name {  color: white !important; font-size: 18px !important; font-family: 'georgia';}

.note_item {font-family: sans-serif !important; font-size: 14px !important; 
line-height: 20px !important;
background: -moz-linear-gradient(bottom, #a25b3f 0%, #d7b39b 100%) !important;
background: -webkit-linear-gradient(bottom, #a25b3f 0%, #d7b39b 100%) !important;
background: -o-linear-gradient(bottom, #a25b3f 0%, #d7b39b 100%) !important;
background: linear-gradient(bottom, #a25b3f 0%, #d7b39b 100%) !important;
color: white !important; box-shadow: 0px 0px 15px #612912 !important; text-shadow: 0px 0px 2px #fff !important;}


pre {background:transparent !important;}

/*
blockquote {
font-family: 'Raleway';
border: 1px solid #fff !important; padding: 10px 10px 15px 15px !important;

background: -moz-linear-gradient(bottom, #d98623 0%, #ebb471 100%) !important;
background: -webkit-linear-gradient(bottom, #d98623 0%, #ebb471 100%) !important;
background: -o-linear-gradient(bottom, #d98623 0%, #ebb471 100%) !important;
background: linear-gradient(bottom, #d98623 0%, #ebb471 100%) !important;

border-radius: 50px 0px 50px 0px; text-shadow: 0px 0px 5px #fff;  margin: 0 !important; font-size:11px !important;}
*/







/*tumblr logo*/

#logo {
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  margin-left: 30px !important;
  padding-left: 220px !important;
  padding-top: 80px !important;
  background-image: url("http://dl.dropbox.com/s/90grc7kqq7273y7/logo.png") !important;
  background-repeat: no-repeat !important;}
  
  




/*new post icons bar*/

ol#posts #new_post {
background: url('http://dl.dropbox.com/s/7h11wxsqzhy0mun/Navigation.png')  top center no-repeat !important;
box-shadow: 0px 0px 0px #fff !important;
border: 0px solid #fff !important;
border-radius:0px !important; height: 100px !important;
text-align:center !important;
margin:0px 0px 0px 0px !important; padding: -50px 0px 0px 0px !important; }


.new_post_label { color: transparent !important;
font-size:0px!important; padding-top:80px !important;
font-family:'Handlee' !important; text-transform:uppercase!important; 
text-shadow: 0px 0px 5px #f46a7f !important; margin-left: -1px !important; }

.new_post_label > I {background-image:none !important;}
.new_post_label > I:before {content: none !important;}






/* Page activité */
.notifications_page .notification_pane { background: transparent !important;}
.active { color: #bbb !important; }

.avatar_frame {
border: 3px solid #dabea6 !important;box-shadow: 0px 0px 2px #edbc6f !important; border-radius: 0px !important;}

.ui_notes {background: #d4c4a2 !important; }	 
.ui_note {border: 1px solid white !important; font-size: 11px !important;}

.notification  {
font-family: 'Raleway'; text-transform:uppercase!important;
border: 0px solid #fff !important; border-radius: 20px !important;
background: #d4c4a2 !important;
text-shadow: 0px 0px 5px #fff; color: #605e5e !important; font-size: 9px !important;}

.notification :link, .notification :active, .notification :visited {color: #883958 !important; text-decoration: none !important; border: none !important;
}





.post_avatar {border: 1px solid #dabea6 !important;
border-radius:0px !important;
box-shadow: 0px 0px 2px #dabea6 !important;}


/* Place, forme des images */
.image { display: block !important;
margin-left: auto !important;
margin-right: auto !important;
border-radius:0px !important;}

.photoset img {border-radius:0px !important;}




/* RETURN TO TOP */
div.return_to_top_icon{	background: none !important;
background-image: url('http://img11.hostingpics.net/pics/779176Top.png') !important;
width: 50px !important;
height: 50px !important;
margin-top: 50px !important;
border-bottom: solid 0px #fff !important;
border-right: 0px solid transparent !important;
border-left: 0px solid transparent !important;}






/* Les posts */
li.post, .post_full, .post, #post_question{
background: #fffdf8 !important;
border-radius: 0px !important;
border: 2px solid #bbb !important;}

.link_button { background: #fff !important; border: none !important;}



/*Colonne droite*/

.controls_section { border-radius: 20px !important;
background: transparent !important; }

.section_header, .currently_selected_blog  { 
color: #fff9f0 !important; letter-spacing: 2px; text-align: center !important; 
border-bottom: 1px solid !important; font-family: 'georgia';
}

.controls_section a {}

/* Les petites flèches ans la colonne de droite */
.sub_control  {color: #fff !important;}

LABEL, .count {}






/*tags*/


.post_tags a:hover {-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear; background:#be640f !important; cursor:help; color: #fff !important; border-radius: 0px;}

.post_tags a {font-size:9px!important;margin-top:0px!important; letter-spacing: 0px !important;
font-family:'trebuchet ms';text-transform:uppercase  !important;}

.post_tags, .post_tags a, .post_tags a:active, .post_tags a:hover, .post_tags a:visited
 { border-radius: 10px !important; padding: 0px 5px 0px 5px !important; }

.post_source{font-size:10px!important; text-transform:uppercase; font-family:'trebuchet ms'!important;}

.footer_links {margin-top:20px!important; vertical-align:middle !important;}

.post_info A,
.post_controls A,
.footer_links.with_tags SPAN A {
color:#333333 !important;}

.token {border: 0px solid #fff !important; 
background-color:#d1d9db!important;}



/* Charger posts */
#previous_page_link, #next_page_link, .add_and_remove,
.mass_editor, .MissingE_marklist_title, .customize,
.recessed, .answer_avatar, .flat, .dashboard_options_form,
#auto_pagination_loader{background:#FFFFFF !important; color: #be5477 !important;
text-shadow: none !important;}





/* Flèches dans le header */
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 4px #ffffff !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px #ffffff !important;}





