css:
@-moz-document url-prefix("http://edushop.bildung.at/edu/"), url-prefix("https://edushop.bildung.at/edushop/") {
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 300;
  src: local('Roboto Light'), local('Roboto-Light'), url(http://themes.googleusercontent.com/static/fonts/roboto/v11/Hgo13k-tfSpn0qi1SFdUfT8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}

@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto Regular'), local('Roboto-Regular'), url(http://themes.googleusercontent.com/static/fonts/roboto/v11/2UX7WLTfW3W8TclTUvlFyQ.woff) format('woff');
}

#main, #box-footer, #loginMedthods {
  margin: 0 auto;
  background-image: none;
}

body {
  background-image: none;
  font-family: 'Roboto', sans-serif !important;
  background-color: rgba(229, 229, 229, 0.37);
  width: 100%;
  margin: 0px !important;
  color: #000;
}

html { width: 100%; }

.box-top, .box-bottom, #box-school-home>.box-content {
  /* Override more specific selectors with important */
  background-image: none !important;
}

.box-content {
  padding: 10px;
}
  

#header {
  background-color: #FFF;
  margin-top: 20px;
  color: #FFF !important;
  margin: 0 auto;
}

#box-footer>.box-bottom, #box-footer.box-top, #box-content.box-content {
  background-color: #FFF !important;  
  background-image: inherit;
}

#box-footer {
  padding: 0px;
  width: 100%;
  margin-left: 0px !important;
  left: 0px;
  -moz-box-shadow: none;
  -webkit-box-shadow: none;
  box-shadow: none;  
  background-image: inherit !important;
}

#box-footer>.box-content { background-color: #FFF; background-image: inherit; }

.login:hover {
  text-decoration: underline;
}

#header>.right>.nav>a { color: #FFF !important; }
#header>.right>.nav>a:hover { text-decoration: underline; }

.type-post, #box-welcome, .box {
  -moz-box-shadow: 0px 2px 10px 0px rgba(0, 0, 0, 0.26);
  -webkit-box-shadow: 0px 2px 10px 0px rgba(0, 0, 0, 0.26);
  box-shadow: 0px 2px 10px 0px rgba(0, 0, 0, 0.26);
  margin-top: 20px; 
}
 
#box-welcome>.box-top { height: 0px; margin-top: 100px;}  
#main>.container-left:last-child { margin-top: 50px; }
#box-infos>.box-top { height: 0px; }
#box-infos { margin-top: 0px; }
    
.box-top, .box-bottom { height: 0px !important; }

#main {
	height: auto;
}

#box-open-payments { margin-top: 100px; }
#box-closed-payments { margin-top: 35px; }

.container-700 { margin-top: 30px; }

#box-school-home {
  background-color: #FFF !important;
  height: 210px !important;
}

#box-school-home div {
  padding-left: 0px !important;
  padding-top: 26px !important;
  background: none !important;
  height: 173px !important;
}

#box-school-home img {
  position: inherit !important;
  top: 0px !important;
  left: 0px !important;
  width: 180px !important;
  height: 155px !important;
  float: left !important;
  margin-left: 20px;
}

#box-school-home h2 {
  font-size: 2em !important;
  font-weight: 300 !important;
  font-family: "Roboto" !important;
  margin-left: 458px !important;
}

#box-school-home a.state-link::before {
  content: "Bundesland: ";
}

#box-school-home a {
  font-size: 1.3em;
  margin-left: 258px;
  text-decoration: none;
}

.box-light ,.box-content {
	background-color: #FFF !important;
}

.box-content h2:first-child {
  background-color: #00bcd4;
  color: #FFF;
  display: block;
  margin-left: -10px;
  margin-right: -10px;
  margin-top: -10px;
  font-size: 1.3em;
  padding: 8px;
  font-family: "Roboto" !important;
  font-weight: 400;
}
    
.box-content h2:first-child > a {
  color: #FFF;
}
        
#box-infos .box-content ul li a:hover {
  border: 1px solid white;
  background-color: inherit;
}

#box-welcome p:last-child {
  line-height: 1.5em !important;
}

ul { list-style: none; }

#box-welcome .box-content > div > div > p::before {
  content: "edu.card";
  display: block;
  margin: -20px;
  background-color: #00BCD4;
  margin-bottom: 20px;
  color: #FFF;
  font-family: "Roboto";
  font-size: 1.3em;
  padding: 10px;
  padding-left: 20px;
}

#box-welcome .box-content > div > p img {
  margin-top: 62px !important;
}

body #header .right .login a {
  font-weight: 400;
}

#box-infos .box-content { padding-bottom: 10px; }

#box-infos::before {
  content: "Menü";
  display: block;
  background-color: #00BCD4;
  color: #FFF;
  font-family: "Roboto";
  padding: 8px;
  padding-left: 10px;
  font-weight: 400;
}

#box-school-home::before {
  content: "Schule / Bundesland";
  display: block;
  background-color: #00BCD4;
  margin-bottom: -28px;
  color: #FFF;
  font-family: "Roboto";
  font-size: 1.3em;
  padding: 7px;
  /*padding-left: 20px;*/
}
}

@-moz-document url("http://edushop.bildung.at/edu/") {
#main>:first-child { margin-top: -80px !important; }
}

@-moz-document url-prefix("http://edushop.bildung.at/edu/schule/") {
#main>:first-child { margin-top: 0px; }
#main>:first-child>div { background-color: #7EAED9; }
#main>:first-child>div>div>h2 { font-family: 'Source Sans Pro', sans-serif; color: #000; }
@font-face {
  font-family: 'Source Sans Pro';
  font-style: normal;
  font-weight: 400;
  src: local('Source Sans Pro'), local('SourceSansPro-Regular'), url(https://themes.googleusercontent.com/static/fonts/sourcesanspro/v7/ODelI1aHBYDBqgeIAH2zlBM0YzuT7MdOe03otPbuUS0.woff) format('woff');
}
}
