css:
@-moz-document url-prefix("http://derstandard.at/Jetzt/Livebericht/") {
#split-screen .comment-container {
   font-size: 75%;
}

#split-screen .user-post .header, #split-screen .user-post .body, #split-screen .user-post .toolbar {
   padding: 2px; 
}

#split-screen .user-post .header {
/*  line-height: 2px; */
}

#split-screen .user-post {
  padding-left: 5px;  
}

div .utools, div.l {
   float: left;
   display: inline;
}

.comment-container .toolbar .postingCount {
   font-size: 130%;        
}
      
#B .comment-container .toolbar, #split-screen #redContentPreview .comment-container .toolbar {
   height: inherit;
   padding: 0px;
   border-top: 0px;
}

#B .comment-container .toolbar>div, #split-screen #redContentPreview .comment-container .toolbar>div {
   position: inherit; 
}

#split-screen #B .comment .hover-element, #split-screen #redContentPreview .comment .hover-element {
   padding: 0px;
      }

#split-screen .header .rating {
  margin-top: inherit;  
}

div .seperator {
   border: 0px;
   padding: 0ox;
}
      
div.separator {
  display: none;
}
      
.comment-container hover-element selected expanded {
          
}
        
div .toolbar {
   border: 0px;
   padding: 0ox;
}

#split-screen .comment .std-button.icon,#split-screen .icon.rate.p,#split-screen .icon.rate.n {
   height: 12px;
   width: 12px;
   line-height: inherit;
   background-image: none;
   margin: 2px;
   margin-top: 5px;
}
.std-button.discreet, .livebericht .comment .std-button, .livebericht .comment .std-button.icon {
/*   border-color: inherit;*/
   background-color:inherit;
}
    
.std-button.icon,.std-button {
  text-indent: 0px;
  line-height: inherit;
  border: 0px;
}

#split-screen .thread .r {
  margin-top: 0px;
}

.std-button.discreet, .livebericht .comment .std-button, .livebericht .comment .std-button.icon {
   font-size: 75%;
}

#split-screen #B .comment .header {
  margin-bottom: .5em;
}

#split-screen .comment .utools .follow {
  margin: 2px; 
}

#split-screen .comment .utools,#split-screen #B .comment .utools {
  height: inherit;
}

#split-screen #B .comment .date .relative, #split-screen #B .comment .date.recent .absolute, .no-touch #split-screen #B .comment .date.recent:hover .relative {
  display: inherit; 
}

#split-screen #B .editor {
  height: inherit;
  margin-top: 0px;
}

#split-screen .user-post .header {
  border-bottom: none; 
}

#split-screen .comment .date.recent .relative, .no-touch #split-screen .comment .date.recent:hover .absolute {
  display: block; 
}
}
