css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("newzleech.com") {

td.clear{width:0px !important; background-color:#000 !important;}

* {color:#fff !important;font-family:tahoma;font-size:12px;}
body {background-color:#000 !important;}
input,select,option {color:#000 !important; background-color:#fff !important;}

span.head{display:none;}
span.light{display:none;}

pre{background-color:#000;font-family:terminal;color:#07d !important;}

a.content,a.skip {
  display:inline !important;padding:2px 8px !important; margin-right:-7px;
  border:1px solid #07d !important;
  background-color:#05b !important ;
  color:#fff !important;
}
a.content:hover,a.skip:hover{
  border:1px solid #09f !important;
  background-color:#07d !important ;
}
a.content b,a.skip b{
  display:inline;margin:1px -9px -3px -9px !important;padding:1px 8px 2px 8px;
  border:1px solid #f90; background-color:#d70;}
a.content b:hover,a.skin b:hover{
  background-color:#f90;}

a.content b,a.skip b {}

form{margin-top:7px !important;}


table[width="806"], table[width="595"] {}
td[bgcolor="#FFFFFF"] {background-color:#222!important;}


table[bgcolor="#CECEE7"] td {
  background-color:#000 !important;}

tr.title td {color:#f90 !important;background-color:#000 !important;font-size:20px;}
td.head {color:#aaa !important;}
td.info {color:#05b !important; border:0px;}
td.info a {color:#07d !important; text-decoration:underline !important;}
td.info a:hover {color:#09f !important; text-decoration:underline !important;}


/* browse table */
table {background-color:#000 !important;}
table tr.headrow, table tr.head {
  color:#fff !important;
  background-color:#222 !important;
 }

table tr.content    
  {background-color:#070707 !important;color:#f0f0f0 !important;}
table tr.contentcol 
  {background-color:#0f0f0f !important;color:#f0f0f0 !important;}

table tr.content, table tr.contentcol {
  border-right:1px solid #222 !important;
  border-left: 1px solid #222 !important;}

tr[bgcolor="#ffffff"] {background-color:#fff !important;}


table tr.content a,table tr.contentcol a
  {color:#07d !important;}
table tr.content a:hover,table tr.contentcol a:hover
  {color:#09f !important;text-decoration:underline !important;}

table tr.content:hover,table tr.contentcol:hover
  {background-color:#07d !important;color:#fff;}
table tr.content:hover a,table tr.contentcol:hover a
  {color:#fff !important;}

table tr.content td b,table tr.contentcol td b{
  color:#f90 !important; 
  border-bottom:1px dashed #ff0;
}


/* post links */
/* table[width="98%"],table[width="98%"] td
  {background-color:#000 !important;}
*/

table.profile    {background-color:#f90 !important; padding:0px !important;}
table.profile td {background-color:#d70 !important;width:auto !important;}
table.profile a        {color:#050505 !important;}
table.profile a:hover  {color:#000 !important; text-decoration:underline !important;}

table.head td {background-color:#000 !important;}

table.head a {
  display:block; padding:2px 4px; margin-bottom:-7px; margin-top:-3px;
  text-align:left;padding-left:10px;
  color:#fff !important;
  background-color:#05b !important;
  border:1px solid #07d !important;}
}
table.head a:hover{
  background-color:#07d !important;
  border:1px solid #09f !important;
}
table.head span.subcat a{margin-left:10px;margin-top:-6px !important;}


/* post table */
table tr.head{background-color:#000 !important;}
table tr.content[bgcolor="#ffffff"] {background-color:#070707 !important;}
table tr.content[bgcolor="#F9F9FF"] {background-color:#0f0f0f !important;}
table tr.content[bgcolor="#ffffff"]:hover, table tr.content[bgcolor="#F9F9FF"]:hover
  {background-color:#07c !important;}

td[background="side.gif"]{background-image:none !important;}

td[width="773"] {
  background-image:url("data:image/gif;base64,%3D") !important;
  background-repeat: no-repeat;}
img[src="title.gif"] {
  visibility:hidden;}




td[background="nav_back.gif"]{
  background:url("data:image/gif;base64,%3D") no-repeat;}
img[src="nav.gif"] {

}
