css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tieba.baidu.com") {
/* --- 导航栏 --- */
.search_nav{
	position:absolute ;
	padding:8px 0!important;
	background:#33373B ;
	color: white;
	width:100% ;
	left:0 ;
	top:0 ;
	}
.search_nav >a , .search_nav >b{
	margin:0;
	border-right:1px solid #555;
	padding:8px 13px 8px 11px;
	color:#C0C0C0 !important;
	font-weight:bold;
	text-decoration:none !important;
	}
.search_nav >b{color:white !important;}

/* 用户信息 */
/*for Tieba Multiuser*/
#com_userbar > ul:nth-child(1) > li:nth-child(1) > div:nth-child(1) > a:nth-child(1){
    margin-top:-3px !important;
    color: #C0C0C0 !important;
    background: none !important;
}
    
.userbar{padding-right:7px ;}
.u_username_avatar{border-radius:10px !important;}
.u_username_wrap{padding-top:3px !important;}
.u_wallet , .u_tbmall ,.userbar .split ,.u_tshow ,.u_mytbmall {display:none !important;}
.u_username_title{color:#C0C0C0 !important;}
.u_setting_wrap{
    background: url('data:image/png;base64,')  transparent;
}
.u_news_wrap{
    background: url('data:image/png;base64,') ;
}
.u_menu_hover >.u_setting_wrap{
        background: url('data:image/png;base64,')  transparent;
}
.u_menu_hover >.u_news_wrap{
     background: url('data:image/png;base64,')  transparent;
}
.u_menu_hover ,.u_username .u_menu_hover .u_username_title {
    color:#537cc4 !important;
    border-color:  #5a8bd7;
}
.u_ddl_con {
    border-color:  #5a8bd7;
}
    

/*LOGO*/
.search_logo{display:none !important;}
#head{padding-top:50px;}
    
/*Search*/
input#wd1 ,.tbsug_expand{border:1px solid #CECECE!important;}
   
/* --- 精简 --- */
.head_skin_btn ,.icon_zhuanpan ,.tbui_fbar_props ,.edui-btn-medal ,img[alt="置顶"] ,img.icon_pre ,.icon_meizhi/*妹纸*/
,#thread_theme_5.p_thread.thread_theme_5 /*帖子顶部翻页条*/ 
,.j_jb_ele ,.lzl_jb /*举报*/,.p_tail_font ,.p_tail_txt ,.p_tail_wap /*字体客户端提示*/
,.user-hide-post-down /*对我隐藏该用户帖子三天*/
,.save_face_bg /*挽尊*/
	{display:none !important;}

/* --- 美化 --- */
.lzlFloor{margin-right:4px !important;}/*楼中楼微调*/
.card_top { background: url('http://tieba.baidu.com/tb/static-frs/img/frs_bright/images_bmg/bg_f_top.png') !important;}
/* --- 修复 --- */
.page-container{background:#FFFFFF !important;}
/*[[Topic]]*/
/*[[FloatBar]]*/
}
