css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.google.co.jp") {
    /* Link deco */
    #topstuff #trev a, #ires #rso h3 a, #newsbox .nrg-title a, #newsbox .vsc a, #imagebox_bigimages .irg-title a, #imagebox_bigimages a.irg-footer, #taw a, #extrares a, #bottomads a, #foot #navcnt #nav a, #rhs #mbEnd a, #rhs #lu_pinned_rhs a, #irc_cc .irc_pt
    {text-decoration: underline !important;}
    
    #topstuff #trev a:hover, #ires #rso h3 a:hover, #newsbox .nrg-title a:hover, #newsbox .vsc a:hover, #imagebox_bigimages .irg-title a:hover, #imagebox_bigimages a.irg-footer:hover, #taw a:hover, #extrares a:hover, #bottomads a:hover, #foot #navcnt #nav a:hover, #rhs #mbEnd a:hover, #rhs #lu_pinned_rhs a:hover, #irc_cc .irc_pt:hover
    {text-decoration: none !important;}
    
    /* Link deco - non fix */
    #rhs #kno-result a:hover{text-decoration: underline !important;}
    
    /* Font size */
    #newsbox .nrg-title a, #newsbox h3 a, #imagebox_bigimages .irg-title a, #imagebox_bigimages a.irg-footer
    {font-size: small !important;}
    #ires h3, #res h3, #tads h3, #tadsb h3, #mbEnd h3, #newsbox span.tl a
    {font-size: medium !important;}
    
    /* Ads */
    #tads, #tadsb{background: #fff6ed !important;}
    #tads:not(:empty):after, #bottomads:not(:empty):after{background: none;}
    #taw ._Xu{display: none;}
    #rhs ._kb ._Vf > span:first-child{display: none;}
    #bottomads li.ads-ad ._Xu{display: none;}
    
    /* Contents */
    .st{line-height: 1.5;color:#555;}
