css:
@-moz-document domain("twitch.tv") {
div#small_nav.column, #right_col, #channel .player-column #player, div.editable div.info, .panel, div.chat-settings, div.emoticon-selector-container {
	box-shadow: 0 0 20px rgba(0, 0, 0, 0.5)!important;
}

div.info {
	text-shadow: 3px 3px 4px rgba(0, 0, 0, 0.8)!important;
}

.channel-stats {
	box-shadow: 0 0 20px rgba(0, 0, 0, 0.3)!important;
  	background: linear-gradient(to bottom, #303030 0%,#1A1A1A 100%)!important;
  	text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.6)!important;
}
  
div.ember-view > .panel.masonry-brick {
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.3)!important;
  	background: linear-gradient(to bottom, #252525 0%,#151515 100%)!important;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.6)!important;
}

#small_nav .content, div#right_col > div.stretch > div.tse-scroll-content {
	background: linear-gradient(to bottom, #202020 0%,#0C0C0C 100%)!important;
}

div.chat-interface, ul#right_nav.segmented_tabs, .segmented_tabs li {
	background: linear-gradient(to bottom, #303030 0%,#1A1A1A 100%)!important;
}

#right_col .top #right_nav, ul#right_nav.segmented_tabs, div.top {
	background-color: #000000 !important;
}

/*.ember-chat .chat-messages, div.chat-messages > div.tse-scroll-content {
	background: linear-gradient(to bottom, #303030 0%,#1A1A1A 100%)!important;
}*/

.chat-messages .chat-line:nth-child(2n) {
	/*background-color: rgba(1, 1, 1, 0.15) !important;*/
  	/*margin-left: -20px;
  	padding-left: 20px;
  	margin-right: -20px;
  	padding-right: 20px;*/
}

.ember-chat {
  	border-top: 1px solid black;
  	margin-top: -1px;
	text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.8);
}

span.emoticon, div.badge, li.panel img, span.stat, .filter_icon {
	-webkit-filter: drop-shadow(2px 2px 4px rgba(0, 0, 0, 0.6));
}

a.filter_icon, a.clearfix {
        background-color: transparent !important;
}

.button-simple.primary, .buttom-simple:hover {
  background: -webkit-gradient(linear,left top,left bottom,from(#8266b6),to(#533787));
  background: -moz-linear-gradient(top,#8266b6,#533787);
  background: -o-linear-gradient(top,#8266b6,#533787);
  background: linear-gradient(top,#8266b6,#533787);
  background-color: #6a4e9e;
  border-top: 1px solid #5b3f8f;
  border-left: 1px solid #412771;
  border-right: 1px solid #412771;
  border-bottom: 1px solid #2a1453;
  box-shadow: inset 0 1px 0 rgba(255,255,255,0.1),0 1px 0 rgba(0,0,0,0.15);
  -moz-box-shadow: inset 0 1px 0 rgba(255,255,255,0.1),0 1px 0 rgba(0,0,0,0.15);
  -webkit-box-shadow: inset 0 1px 0 rgba(255,255,255,0.1),0 1px 0 rgba(0,0,0,0.15);
  text-shadow: 0 1px 0 rgba(0,0,0,0.75);
  -moz-text-shadow: 0 1px 0 rgba(0,0,0,0.75);
  -webkit-text-shadow: 0 1px 0 rgba(0,0,0,0.75);
  color: #fff;
}

.following-col .header {
	border-top: 1px solid rgba(255, 255, 255, 0.8);
}
      
#main_col div.directory_header ul.nav.js-nav li a.active,
#main_col div.directory_header ul.nav.js-nav li a.active:hover,
#main_col div.directory_header ul.nav.js-nav li a:hover,
.ember-chat .chat-line a,
.panel-formatting .panel div.part a {
	color: #8266b6 !important;
	border-color: #8266b6 !important;
    text-decoration: none;
}
              
div#channel div.player-column div.stats-and-actions.clearfix div.channel-stats span.stat,
.ember-chat .chat-messages .chat-line.admin .message {
	color: #AAA !important;
}
}
