 * or PM me on http://brassgoggles.co.uk/forum/
 */


@-moz-document url-prefix(http://brassgoggles.co.uk/forum/), url-prefix(http://brassgoggles.org/forum/)
{
/* The site CSS specifies border-bottom instead of underline:-P */
a:link, a:hover {
	border: none ! important;
}

img {
	max-width: 742px ! important;
}

.post, .quote {
	font-family: georgia ! important;
	font-size: 12pt ! important;
	color: #000000 ! important;
	word-wrap: break-word ! important;
}

/* Disalternate the background color of posts */
.windowbg, .windowbg td, .windowbg2, .windowbg2 td, .windowbg3, .windowbg3 td {
	background: #fcf6e0 ! important;
	color: #000000 ! important;
}

.windowbg .smalltext, .windowbg2 .smalltext, .signature, .middletext, small, .windowbg b, .windowbg2 b, .bordercolor td {
	color: #000000 ! important;
}

.bordercolor td.catbg, .bordercolor .titlebg td {
	color: #fedd9c ! important;
}

/* Hide the ugly blue "new posts" icon */
a img[alt="New"] {
	opacity: 0 ! important;
}

/* New brown icon */
a[id^="newicon"] {
	background: url(data:image/gif;base64,) ! important;
}

hr, .hrcolor {
	color: #c99463 ! important;
	background-color: #c99463 ! important;
}

td.windowbg a:link, td.windowbg2 a:link, td.windowbg3 a:link, #preview_body a:link {
	color: #996500 ! important;
}

td.windowbg a:visited, td.windowbg2 a:visited, td.windowbg3 a:visited, #preview_body a:visited {
	color: #662900 ! important;
}

.quote, #preview_body {
	background-color: #f2e5b6 ! important;
	padding: 0.25em ! important;
	border: thin solid #c99463 ! important;
}

.spoiler {
	padding: 0.25em ! important;
	color: #000000 ! important;
	background-color: #e0cfce ! important;
	border: thin solid #d1a7a5 ! important;
}

.post a:link {
	text-decoration: underline ! important;
}

.post a:hover {
	text-decoration: none ! important;
}

.quoteheader a:link {
	text-decoration: none ! important;
}

span[id^="msg"] {
	font-weight: bold ! important;
}

span[style="color: black;"] {
	background-color: white ! important;
}

td.middletext {
	background: none ! important;
	background-color: #e3d49c ! important;
}

body {
	max-width: 1000px ! important;
	margin: auto ! important;
}

a:link img[alt="WWW"] {
	border: 2px solid blue ! important;
}

a:visited img[alt="WWW"] {
	border: 2px solid purple ! important;
}
}
