@-moz-document domain("fitocracy.com") {
body, a:hover {cursor: url(http://cur.cursors-4u.net/cursors/cur-9/cur847.ani), url(http://cur.cursors-4u.net/cursors/cur-9/cur847.png), progress !important;}


/* HEADER MENU */
header:before {
background: #000 !important;
}
header {
background: linear-gradient(to bottom, rgba(0,30,60,1), rgba(0,0,0,1)) !important;
border-bottom: 0px !important;
}
#nav_search {
background: transparent !important;
border: 0px !important;
