css:
/* AGENT_SHEET */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tumview.com") {

/* === Tumblr - Widescreen Tumbview v.2 - 
WITH: https://greasyfork.org/fr/scripts/8-tumblr-tumview-linker
- ==== */

/* GENRAL CONTAINER - === */
body {
    background: #222222 !important;
    display: inline-block ! important;
    float: left !important;
/* background: red !important; */
}

/* RIGHT CONTAINER - 
#userPhotos 
=== */
.rightBar {
    display: inline-block ! important;
    width: 1210px !important;
    height: 100% !important;
    min-height: 680px !important;
    max-height: 680px !important;
    left: 702px !important;
    top: 5px!important;
    padding: 20px 0 200px 0  !important;
/* overflow: visible !important; */
    overflow: hidden !important;
    overflow-y: auto !important;
    background: gray !important;
/* background: red !important; */
}

.imageDetail {
    display: inline-block !important;
    float: right !important;
    margin: 2em 0 5em;
    vertical-align: top !important;
    width: 264px !important;
    min-height: 250px ! important;
    max-height: 250px ! important;
    margin-right: 10px ! important;
    margin-top: 0px ! important;
    border: 1px solid black ! important;
    border-radius: 5px ! important;
    padding: 10px 10px 0 10px ! important;
    overflow: hidden !important;
    overflow-y: auto !important;
    overflow-y: hidden !important;
}

#userPhotos .rightBar .imageDetail .interstatial {
    margin-top: -10px !important;
    text-align: center;
}
#userPhotos .rightBar .imageDetail .image {
    position: relative;
    height: 232px !important;
    max-width: 299px;
    text-align: center !important;
    transition: ease all 0.2s !important;
}
#userPhotos .rightBar .imageDetail:hover .image {
    position: relative;
/* height: 0px !important; */
    transition: ease all 0.7s !important;
}

#userPhotos .rightBar .imageDetail .image img[src*=".jpg"]  ,
#userPhotos .rightBar .imageDetail .image img[src*=".gif"] {
    position: relative;
    display: inline-block !important;
    max-height: 232px !important;
    width: auto !important;
    max-width: 264px !important;
}

#userPhotos .rightBar .imageDetail .inner .meta-data {
/* position: absolute !important; */
    position: relative  !important;
    display: inline-block !important;
    width: 282px !important;
    margin-left: -8px !important;
/* padding: 2px !important; */
    border-radius: 3px 3px 0 0 !important;
/* background: black !important; */
    z-index: 500 !important;
}

#userPhotos .rightBar .imageDetail .inner .meta-data:before {
    content: "▲" !important;
    position: absolute !important;
/* position: relative  !important; */
    display: inline-block !important;
    margin-top: 4px !important;
    border-radius: 3px 3px 0 0 !important;
/*     background: black !important; */
    opacity: 1 !important;
/* z-index: 500 !important; */
}
#userPhotos .rightBar .imageDetail:hover .inner .meta-data:before {
    content: "▲" !important;
    opacity: 0 !important;
/* z-index: 500 !important; */
}

#userPhotos .rightBar .imageDetail .inner .meta-data .tags ,
#userPhotos .rightBar .imageDetail .inner .meta-data .caption {
    display: inline-block !important;
    width: 276px !important;
    height: 20px !important;
    padding: 2px !important;
    border-radius: 3px 3px 0 0 !important;
    background: rgba(0, 0, 0, 0.5) !important;
    overflow: hidden !important;
    overflow-y: hidden !important;
    transform: translate(0px, 18px) !important;
    transition: 0.3s ease 0s all  !important;
}
#userPhotos .rightBar .imageDetail:hover .inner .meta-data .tags ,
#userPhotos .rightBar .imageDetail:hover .inner .meta-data .caption  {
    height: 100px !important;
    overflow-y: auto !important;
    transform: translate(0px, -84px) !important;
}


#userPhotos .rightBar .imageDetail .image a.fullsize {
    position: absolute;
    display: block;
    border-radius: 3px;
    right: -8px !important;
    top: -9px !important;
    padding: 0px 2px !important;
    transition: opacity 0.3s ease 0s;
    background: rgba(0, 0, 0, 0.23) !important;
/* opacity: 0.5 !important; */
    z-index: 500 !important;
}


/* LEFT MINI THUMBS - === */
#content {
    display: inline-block !important;
    height: 100%;
    width: 702px !important;
/* overflow: visible !important; */
}
#userPhotos .leftBar  {
    display: inline-block !important;
    width: 700px !important;
    height: 100% !important;
    min-height: 500px !important;
    max-height: 500px !important;
    padding: 20px 0 200px 0  !important;
/* overflow: visible !important; */
    overflow: hidden !important;
    overflow-y: auto !important;
/* background: blue !important; */
}

#userDetails {
    width: 660px !important;
    height: 14px;
white-space: nowrap !important;
text-transform: capitalize !important;
    overflow: hidden !important;
text-overflow: ellipsis !important;

}
#userPhotos .leftBar .photo {
    position: relative !important;
    display: inline-block !important;
    height: 80px !important;
    left: 5px !important;
    text-decoration: none;
    top: -10px !important;
/* overflow: visible !important; */
    overflow: hidden !important;
    background: black !important;
/* background: green !important; */
}

#userPhotos .leftBar .photo table {
    margin: 0 auto;
    width: 119px !important;
    height: 91px !important;
}

#userPhotos .leftBar .photo img {
    max-height: 100px;
    max-width: 100% !important;
}


#userPhotos .loader {
    position: fixed;
    bottom: -29px;
    top: 887px !important;
    left: 25%;
    margin-left: -45px;
    padding: 10px 15px 5px;
    border-radius: 8px 8px 0 0;
    background: #222 !important;
    transition: bottom 0.3s ease 0s;
    z-index: 10;
}

#photoDetailWrapper {
    background: rgba(0, 0, 0, 0.8) !important;
    z-index: 200 !important;
}

/* BY TAGS  */
.page.active + .page.active  #photoDetailWrapper {
    background: rgba(0, 0, 0, 0.8) !important;
    z-index: 2000 !important;
}

.page.active {
    width: 1900px !important;
/* background: green !important; */
}
.taggedPostsWrapper{
position: relative !important;
display: inline-block !important;
    min-width: 1900px !important;
    max-width: 1900px !important;
background: green !important;
}
.results {
position: relative !important;
display: inline-block !important;
    height: 5251.53px;
    position: relative;
    min-width: 1900px !important;
    max-width: 1900px !important;
}
 .result {
/* position: relative !important; */
display: inline-block !important;
/* float: left !important; */
/* clear: both !important; */
    width: 200px !important;
height: 150px !important;
overflow: hidden !important;
    left: 0;
    top: 0;
}

.post.link {
    position: relative;
    display: inline-block !important;
    height: 132px !important;
    max-width: 204px !important;
    text-align: center !important;
    transition: ease all 0.2s !important;
}
.taggedPostsWrapper .result .post.author {
    position: absolute;
    display: inline-block !important;
    width: 201px !important;
    right: 0;
    line-height: 1.25em;
    top: -17px !important;
    left: -9px !important;
    margin: 10px;
    font-size: 12px;
    text-align: center;
    color: rgba(255, 255, 255, 0.95);
    background-color: rgba(0, 0, 0, 0.8);
    overflow: hidden;
    word-wrap: break-word;
}

.post.link>img[src*=".jpg"]  ,
.post.link>img[src*=".gif"] {
    position: relative;
    display: inline-block !important;
    max-height: 132px !important;
    width: auto !important;
    max-width: 200px !important;
margin-top: 10px !important;
}

.post.text  {
/* position: absolute !important; */
/*     position: relative  !important; */
    display: inline-block !important;
    width: 205px !important;
/*     margin-top: 203px !important; */
    margin-left: -2px !important;
/* padding: 2px !important; */
    border-radius: 3px 3px 0 0 !important;
background: red !important;
    z-index: 500 !important;
}
.taggedPostsWrapper .result .post.title {
    position: absolute !important;
    display: inline-block !important;
    width: 204px !important;
    height: 20px !important;
    right: 0 !important;
    top: 20px !important;
}
.post.description {
    position: absolute !important;
    display: inline-block !important;
    width: 204px !important;
    height: 15px !important;
    right: 0 !important;
top: 141px !important;
    padding: 2px !important;
    border-radius: 3px 3px 0 0 !important;
    background: rgba(0, 0, 0, 0.5) !important;
    overflow: hidden !important;
    overflow-y: hidden !important;
/* transform: translate(0px, 18px) !important; */
    transition: 0.3s ease 0s all  !important;
}


/* VIEWER */
.page.active +.page.active #photoDetailWrapper{
/* background: blue !important; */
}


#photoDetailWrapper .photo {
    transition: all 0.3s ease 0s;
    z-index: 800 !important;
    pointer-events : none !important;
}
#photoDetailWrapper .photo .expander {
    pointer-events: initial !important;
}
#photoDetailWrapper .photo .autosizingImage img {
    position: absolute;
    transition: all 0.3s ease 0s;
    z-index: 10;
    pointer-events: initial !important;
}

#photoDetailWrapper .details ,
#photoDetailWrapper.contentVisible .details {
    z-index: 100 !important;
    pointer-events : none !important;
}
#photoDetailWrapper .details>div ,
#photoDetailWrapper.contentVisible .details>div {
    pointer-events: initial !important;
    z-index: 800 !important;
}

.page.active +.page.active #photoDetailWrapper .close {
    position: absolute;
    display: inline-block !important;
    width: 1878px !important;
    min-height: 95% !important;
    right: 34px;
    top: 3px;
/* cursor: pointer !important; */
/* z-index: 100 !important; */
/* background: red !important; */
}

#photoDetailWrapper .close a {
    position: absolute !important;
    display: inline-block !important;
    min-height: 897px !important;
    min-width: 1878px !important;
    max-height: 700px !important;
    border-bottom: 0 none;
/* background: peru !important; */
/* cursor: pointer !important; */
} 

#photoDetailWrapper .close a img  {
    position: absolute !important;
    display: inline-block !important;
    max-height: 900px !important;
    min-height: 900px !important;
    min-width: 1878px !important;
/* margin-left: 955px; */
    margin-top: -10px !important;
    opacity: 0 !important;
/* cursor: pointer !important; */
    cursor: url("https://cdn1.iconfinder.com/data/icons/fugue/bonus/icons-24/yin-yang.png"), auto  !important;
/* z-index: 200 !important; */
}
/* #photoDetailWrapper .close:hover a img  {
    opacity: 1 !important;
cursor: pointer !important;
} */



/* ENLARGE ICON txt - === */
a.m {
    background-image: url("data:image/png;base64,");
    background-position: 0 0;
    display: block;
    float: right;
    font-size: 0 !important;
    height: 22px;
    margin: 8px;
    text-decoration: none;
    text-indent: -999px;
    width: 22px;
}

/* END === */

}
