@-moz-document domain("worth1000.com") {
 body, a:hover {cursor: url(http://cur.cursors-4u.net/cursors/cur-11/cur1014.cur), progress !important;
background: #000 !important;
} 

.header_message_container {
background: linear-gradient(to bottom, rgba(0,30,60,1), rgba(0,0,0,1)) !important;
border-bottom: 0px !important;
}
#header_container {
background: #000 !important;
}
#top_nav_logo_img a img {
background: #000 !important;
border: 0px !important;
}
  
#wrapper_middle_header_container {
background: linear-gradient(to bottom, rgba(0,30,60,1), rgba(0,0,0,1)) !important;
}
#cat_menu ul li {
background: transparent !important;
}
#cat_menu ul li.selected a, #cat_menu ul li.selected a:hover {
background: linear-gradient(to bottom, rgba(0,0,0,1), rgba(0,30,60,1), rgba(0,30,60,1)) !important;
text-shadow: 0 0 5px #fff !important;
}
#cat_menu ul li a {
background: linear-gradient(to bottom, rgba(255,255,255,0.8), rgba(50,50,50,1), rgba(0,40,90,1), rgba(0,30,60,1), rgba(0,30,60,1)) !important;
color: #fff !important;
}
#cat_menu ul li a:hover {
text-shadow: 0 0 5px #fff !important;
background: linear-gradient(to bottom, rgba(0,200,255,1), rgba(50,50,50,1), rgba(0,40,90,1), rgba(0,30,60,1), rgba(0,30,60,1)) !important;
}
#banner h1 a b {
background: transparent !important;
border-top: transparent !important;
border-left: transparent !important;
border-bottom: transparent !important;
border-right: transparent !important;
}
#bg_container {
background: #000 !important;
}
#wrapper_bottom_header_container {
border-bottom: 0px !important;
background: linear-gradient(to bottom, rgba(0,0,0,1), rgba(0,30,60,1), rgba(0,0,0,1)) !important;
}
#wrapper_bottom_header_container:hover {
border-bottom: 0px !important;
background: linear-gradient(to bottom, rgba(0,0,0,1), rgba(0,40,70,1), rgba(0,0,0,1)) !important;
}
#active_container {
background: #000 !important;
}
#menu ul li.selected a, #menu ul li.selected a:hover {
background: linear-gradient(to bottom, rgba(0,0,0,1), rgba(0,80,180,1), rgba(0,0,0,1)) !important;
color: #fff !important;
}
#menu ul li a {
color: #fff !important;
border-left: 1px solid #000 !important;
border-right: 1px solid #000 !important;
}
#menu ul li a:hover {
color: #fff !important;
text-shadow: 0 0 3px #fff;
background: linear-gradient(to bottom, rgba(0,0,0,1), rgba(0,80,180,1), rgba(0,0,0,1)) !important;
}
a:link.pill, a:visited.pill {
background: rgba(255,255,255,0.3) !important;
}
#user_login a {
color: #fff !important;
border: 1px #337695 !important;
}
input, input[type="password"], input[type="search"], isindex {
background: #1d1d1d !important;
color: #fff !important;
border: 2px inset #003954 !important;
}
.btn span, .btn-disabled span {
background: linear-gradient(to bottom, rgba(200,200,200,1), rgba(50,50,50,1)) !important;
color: #fff !important;
}
keygen, select, select[size="0"], select[size="1"] {
background: #323232 !important;
color: #fff !important;
}
#topic_menu {
background: rgba(0,0,0,1) !important;
}
  
  
#top_nav_logo_img a img
{ width: 0 !important;
padding-left: 155px !important;
background: url(data:image/png;base64,) no-repeat !important; }
  
.callout {
background: linear-gradient(to bottom, rgba(255,255,255,0.1), rgba(0,80,180,0.1)) !important;
border: 0px !important;
color: #fff !important;
}
.thumbs_title h1 {
color: #fff !important;
}
a:link.pill, a:visited.pill {
color: #fff !important;
}
.active_thumb a .timer {
background: transparent !important;        
}
#wrapper_content {
background: #000 !important;
}
#title_left_content {
border: 0px !important;
background: #000 !important;
}
#title_h1 h1 {
color: #fff !important;
}
#about, #about p, .module-header, #about h1, #info {
background: #000 !important;
border: 0px !important;
color: #fff !important;
}

            
            #facebox .fbw, #facebox .body  {
                background: rgba(255,255,255,0.2) !important;
              }
 h1, h2, h3, h4, h5, h6, ul, ol, li, form, body, span.frm-bad, span.frm-good, a:link, a:visited {
              color: #fff !important;
            }

#arena_menu, #arena_menu ul li.selected a, #arena_menu ul li.selected a:hover, #arena_menu ul li a:hover, #arena_menu ul li a {
  background: #000 !important;
}

/* WHEN LOGGED IN*/


#user_logged_in ul.main_user_options li {
border-left: 0px !important;
border-right: 0px !important;
}
#user_login_settings, #user_login_settings ul li a{
  background: rgba(0,0,0,1) !important;
  color: #fff !important;
  border: 0px !important;
}
#user_login_settings ul li a:hover, #my_button a {
  background: rgba(255,255,255,0.2) !important;
}



/* FORUM */

.forum-item.head {
background: linear-gradient(to bottom, rgba(0,30,60,1), rgba(0,0,0,1)) !important;
    }
    .forum-item {
border-bottom: 1px solid #323232 !important;
}
      .forum-item.sticky {
background: linear-gradient(to bottom, rgba(30,0,80,1), rgba(0,0,0,1)) !important;
          }
          .forum-item-r a {
              background: transparent !important;
              border: 1px #272727 !important;
            }
            .call-out a, .call-out-secondary a, .paging-extra a {
                background: #373737 !important;
                color: #fff !important;
                border: 5px #414141 !important;
              }
              .sites ol li a {
background: linear-gradient(to bottom, rgba(0,30,60,1), rgba(0,0,0,1)) !important;
                }
                .sites ol li.selected {
                text-shadow: 0 0 5px #fff !important;
                  }
                  .gallery-list div a {
                      background: #000 !important;
                    }
                    .arenalist {
                        background: rgba(255,255,255,0.2) !important;
                      }
                      .thread-item {
background: #000 !important;
}
                        form.form textarea, div.form textarea {
                          background: #1d1d1d !important;
                          color: #fff !important;
                        }
                        
                        
                        
                        /* MEMBERS */
                        
                        .hof td {
border-bottom: 1px solid #1c1c1c !important;
border-right: 1px solid #1c1c1c !important;
                          }
                          .hof td:hover {
                                background: #000 !important;
                              }
.alt1, .alt2 {
                                  background: #000
                                }
                                
.timer {
background: #000 !important;
                                  }
                             .rules {
                                      background: #000 !important;
                                    }
                                    p {
                                      color: #fff !important;
                                    }
                                    
.help-item.sm, .help-item.bg, .help-item li, .help-item li a {
background: #000 !important;
}
                                        .no-results div, .no-results {
                                            color: #fff !important;
                                          }
                                          .vote-tbl {
                                              background: #000 !important;
                                            }
                                            
                                            
                                            /* PROFILE */
                                            ul.bullet-blue li, .site-list, div.mtc.unearned {
                                              background: #000 !important;
                                            }
/* RULES*/
                                            .da-message, #ph_faq, #ph_faq table, #ph_faq #t_VotingScale td, #ph_faq #t_GraduationChart td, #ph_faq .quicklink {
                                                background: #000 !important;
                                                color: #fff !important;
                                              }
