css:
@-moz-document domain("duckduckgo.com") {
/*==============================
Main background
==============================*/
HTML {
  background:#111;
}

body {
  color:#999;
  background:rgba(0,0,0,0.00);
}



/*==============================
Main search area
==============================*/
#logo_homepage {
background: url("http://i.imgur.com/9nnxUeY.png") no-repeat center top;
width:600px !important;
}

#search_form_input_homepage {
background: #222;
color: #ddd;
border: 1px solid #555;
font-size: 18px;
-webkit-border-radius: 3px 0 0 3px;
border-radius: 3px 0 0 3px;
-moz-border-radius: 3px 0 0 3px;
-webkit-border-top-left-radius: 3px;
-webkit-border-bottom-left-radius: 3px;
-webkit-box-shadow: 0 1px 2px 0 rgba(0,0,0,0.2);
box-shadow: 0 1px 2px 0 rgba(0,0,0,0.2);
-moz-box-shadow: 0 1px 2px 0 rgba(0,0,0,0.2);
-webkit-box-shadow: 0 1px 2px 0 rgba(0,0,0,0.2);
-webkit-box-shadow: 1px 1px 2px #000;
box-shadow: inset 0px 0px 10px #000;
}



/*==============================
Links
==============================*/
a:visited {
color: #b66;
}
a {
  color: #bbb;
  transition:0.40s;
}
a:hover {
  color:#ddd;
  text-decoration:none;
  transition:0.20s;
}
.url, .url a {
color: #686!important;
font-size: 92.8%;
}
.links_menu, .links_menu a {
color: #686!important;
}



/*==============================
Top banner: Background
==============================*/
#header_wrapper #header {
  background: linear-gradient(top,#333 0,#111 100%);
  background: -webkit-linear-gradient(top,#333 0,#111 100%);
  background: -moz-linear-gradient(top,#333 0,#111 100%);
  background: -o-linear-gradient(top,#333 0,#111 100%);
  background: -ms-linear-gradient(top,#333 0,#111 100%);
  background: linear-gradient(to bottom,#333 0,#111 100%);
  border-bottom:#333 solid thin;
  box-shadow:rgba(0,0,0,0.50) 0px 3px 1px;
}


/*==============================
Top banner: Search area
==============================*/
#search_form_input {
  background: #222;
  color: #ddd;
  text-shadow: 0 0 10px #000000;
  border: 1px solid #666;
  font-size: 95.2%;
  -moz-box-shadow:inset 0 0 10px #000000;
   -webkit-box-shadow: inset 0 0 10px #000000;
   box-shadow: inset 0 0 10px #000000;
}

#search_form {
border-bottom: 0px solid #cc6a5f;
}



/*==============================
Top banner: Dropdown
==============================*/
.bangwrap ul {
-webkit-box-shadow: 0 1px 3px 0 rgba(0,0,0,0.2);
-webkit-box-shadow: 0 1px 3px 0 rgba(0,0,0,0.2);
box-shadow: 0 1px 3px 0 rgba(0,0,0,0.2);
background: #000;
border: 1px solid #ddd;
}
  
.bangwrap li.header {
    color:#ccc !important;
    background-color: #333;
    border:none;
}
    
.bangwrap li a {
color: #5a5;
background-color: #222;
}

.bangwrap ul {
border: 1px solid #333;
}

#header_wrapper #header #header_content_wrapper #header_content #header_button_wrapper #header_button #header_button_menu_wrapper #header_button_menu li.header_button_menu_header {
background-color: #333;
color:#ccc !important;
border:none;
}

#header_wrapper #header #header_content_wrapper #header_content #header_button_wrapper #header_button #header_button_menu_wrapper #header_button_menu {
background: #000;
border: 1px solid #333;
}

#header_wrapper #header #header_content_wrapper #header_content #header_button_wrapper #header_button #header_button_menu_wrapper #header_button_menu a {
  background-color: #222;
}


/*==============================
Info
==============================*/
.info {
background: #fef9cd;
border: 1px solid #e1d980;
}



/*==============================
Search results: "Zero clicks"
==============================*/
#special_page_header, #zero_click_header {
color: #aaa;
  text-shadow:1px 1px 3px rgba(0,0,0,0.80);
background-color: #333;
background: -webkit-linear-gradient(top,#333 0,#292929 100%);
background: -moz-linear-gradient(top,#333 0,#292929 100%);
background: -o-linear-gradient(top,#333 0,#292929 100%);
background: -ms-linear-gradient(top,#333 0,#292929 100%);
background: linear-gradient(to bottom,#333 0,#292929 100%);
-webkit-box-shadow: 0px 2px 3px rgba(0,0,0,0.80);
box-shadow: 0px 2px 3px rgba(0,0,0,0.80);
-webkit-border-radius: 3px 3px 0 0;
border-radius: 1px 1px 0 0;
font-size: 107.1%;
border-bottom: thin solid #333;
border-top:none;
border-right:none;
border-left:none;  
}

#special_page_header, #zero_click_header h1 a, #special_page_header h1 a {
color: #aaa;
}

.icon_zero_click_header, #zero_click_plus_wrapper.icon_zero_click_header {
border-left: 1px solid #888;
background-color: #333;
background: -webkit-linear-gradient(top,#333 0,#292929 100%);
background: -moz-linear-gradient(top,#333 0,#292929 100%);
background: -o-linear-gradient(top,#333 0,#292929 100%);
background: -ms-linear-gradient(top,#333 0,#292929 100%);
background: linear-gradient(to bottom,#333 0,#292929 100%);
-webkit-box-shadow: 0px 2px 3px rgba(0,0,0,0.80);
box-shadow: 0px 2px 3px rgba(0,0,0,0.80);
-webkit-border-radius: 3px 3px 0 0;
border-radius: 1px 1px 0 0;
border-bottom: 1px solid #333;
}

.icon_zero_click_header:hover, #zero_click_plus_wrapper.icon_zero_click_header:hover, #special_page_header:hover, #zero_click_header:hover {
    background: linear-gradient(to bottom,#444 0,#2c2c2c 100%) !important;
  border-bottom: thin solid rgba(0,0,0,0.00);
}


.highlight {
  -moz-border-radius: 8px;
  -webkit-border-radius: 8px;
  border-radius: 3px;
  border: 1px solid rgba(255,255,255,0.08) !important;
  transition:0.70s;
  }

#zero_click {
  color:#bbb;
  background: #383838;
  background: linear-gradient(to bottom,#1b1b1b 0,#111 100%);
  border-color: rgba(136,136,136,0.00);
  -moz-border-radius: 8px;
  -webkit-border-radius: 8px;
  -webkit-border-radius: 8px;
  border-radius: 8px;
  z-index: 10;
  text-shadow: 1px 1px 1px rgba(0,0,0,0.80);
  box-shadow: inset 0px 0px 16px #000000 !important;
  box-shadow: 0px 0px 0px #000000;
  transition:0.70s;
}



/*==============================
Search results: Main results
==============================*/
/* Media results*/
#ddgc_pagination {
background: rgba(0,0,0,0.00);
border-top: rgba(0,0,0,0.00);
}

#ddgc_pagination a.npa {
background-color:rgba(0,0,0,0.00);
}

#nexta {
border-left: 1px solid rgba(0,0,0,0.00);
}
  
#preva {
border-right: 1px solid rgba(0,0,0,0.00);
}
  
#ddgc_slider {
border: 1px solid rgba(0,0,0,0.00);
}
  
#ddgcarousel li.ddgc_item:hover {
background:rgba(255,255,255,0.10);
}
    
#ddgcarousel li.ddgc_item:hover {
  border: 1px solid #444;
}
    
#ddgc_pagination.minimal {
border-bottom: none;
background: transparent;
}
    
/* Primary results */
.results_links {
  color:#ddd;
  text-shadow:rgba(0,0,0,0.30) 1px 1px 1px;
  background: -webkit-gradient( linear, left top, left bottom, color-stop(0.00, #222), color-stop(1, #181818) );
  background: -webkit-linear-gradient(top,#222 0,#181818 100%);
  background: -moz-linear-gradient(top,#222 0,#181818 100%);
  background: -o-linear-gradient(top,#222 0,#181818 100%);
  background: -ms-linear-gradient(top,#222 0,#181818 100%);
  padding-left: 5px;
  padding-top: 10px;
  padding-bottom: 10px;
  padding-right: 5px;
  border-left: 8px solid #332!important;
  border-bottom: 0px solid #acf!important;
  border-top: 0px solid #acf!important;
  border-right: 0px solid #acf!important;
  border-radius:8px;
  box-shadow:#000 0px 0px 4px;
  margin-bottom:8px;
}

.results_links:hover {
  text-shadow:rgba(0,0,0,0.30) 1px 1px 1px;
  border-left: 8px solid #553!important;
  border-bottom: 0px solid #acf!important;
  border-top: 0px solid #acf!important;
  border-right: 0px solid #acf!important;
  border-radius:8px;
  box-shadow:#000 0px 0px 4px;
  margin-bottom:8px;
}

/* Secondary results */
.results_links_deep {
  color:#999;
  text-shadow:rgba(0,0,0,0.30) 1px 1px 1px;
  background: -webkit-gradient( linear, left top, left bottom, color-stop(0.00, #222), color-stop(1, #181818) );
  background: -webkit-linear-gradient(top,#222 0,#181818 100%);
  background: -moz-linear-gradient(top,#222 0,#181818 100%);
  background: -o-linear-gradient(top,#222 0,#181818 100%);
  background: -ms-linear-gradient(top,#222 0,#181818 100%);
  padding-left: 5px;
  padding-top: 10px;
  padding-bottom: 10px;
  padding-right: 5px;
  border-left: 8px solid #333!important;
  border-bottom: 0px solid #acf!important;
  border-top: 0px solid #acf!important;
  border-right: 0px solid #acf!important;
  border-radius:8px;
  box-shadow:#000 0px 0px 4px;
  margin-bottom:8px;
}

.results_links_deep:hover {
  text-shadow:rgba(0,0,0,0.30) 1px 1px 1px;
  border-left: 8px solid #444!important;
  border-bottom: 0px solid #acf!important;
  border-top: 0px solid #acf!important;
  border-right: 0px solid #acf!important;
  border-radius:8px;
  box-shadow:#000 0px 0px 4px;
  margin-bottom:8px;
}
.snippet, .snippet a {
color: #888!important;
text-decoration: none!important;
}



/*==============================
"Did you mean?"
==============================*/
#did_you_mean {
border: 1px solid #444;
background:#222;
margin-bottom:8px;
}
#did_you_mean:hover {
border: 1px solid #222;
background:#444;
}



/*==============================
Sidebar
==============================*/
#side {
background-color:#111;
}

#powered_by {
  background:#111;
}

#side .border {
  color: #686771;
  background:#111;
  border:1px solid #111;
  position: fixed;
}

#side .border:hover {
  background:#111;
}

#side_message_ab {
opacity:0.00;
}


/*==============================
Footer
==============================*/
#footer_homepage {
color: #333;
background: -webkit-linear-gradient(top,#222 0,#181818 100%);
background: -moz-linear-gradient(top,#222 0,#181818 100%);
background: -o-linear-gradient(top,#222 0,#181818 100%);
background: -ms-linear-gradient(top,#222 0,#181818 100%);
border-top: 1px solid #333;
}

.modal, .add_to_browser, #footer_homepage .add_to_browser, #footer_homepage .modal {
background-color: #222;
border: 1px solid #444;
}
.modal:before, .add_to_browser:before, #footer_homepage .add_to_browser:before, #footer_homepage .modal:before {
border-top: 9px solid #444;
}

.modal:after, .add_to_browser:after, #footer_homepage .add_to_browser:after, #footer_homepage .modal:after {
border-top: 9px solid #222;
}

.modal h4, .add_to_browser h4, #footer_homepage .add_to_browser h4, #footer_homepage .modal h4 {
color: #ccc;
}

#more_menu li.more_menu_header {
background-color: #1a1a1a;
color: #ccc!important;
border:1px solid rgba(0,0,0,0.00);
}

#more_menu {
background: #1a1a1a;
}

#more_menu li a {
background-color: #222;
}

#more_menu {
border: 1px solid #444;
-moz-border-radius: 4px;
}

#footer_homepage_right #more_menu:after {
bottom: -8px;
border-top: 9px solid #222;
}

#footer_homepage_right #more_menu:before {
bottom: -10px;
border-top: 9px solid #444;
}



/*==============================
Ads
==============================*/
#ads .results_links {
  width:0px;
  height:0px;
  padding:-100px;
  margin:-8px;
  overflow:hidden;
  opacity:0.00;
}

#ads .highlight_sponsored.highlight_sponsored_hover {
background: rgba(255,212,0,0.1);
border-color: rgba(0,0,0,0.4);
cursor: default!important;
left:-99999px;
opacity:0.00;
}
}
