css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.baidu.com/#wd=")
,url-prefix("http://www.baidu.com/baidu?wd=")
,url-prefix("http://www.baidu.com/s?"){
/* 链接样式 */
*{text-decoration:none!important;}
a{color:#33E !important;}
em{color:#c33 !important;}
/* LOGO */
.s_logo{
	position:absolute;
	z-index:555 !important;;
	background:url(http://imgsrc.baidu.com/forum/pic/item/955ff91f3a292df539612cd4bc315c6036a87386.jpg) no-repeat center center!important;
	top:39px !important;
	left:0px;
	padding:11px 17px 0 18px;}
.s_logo >*{opacity:0;}
#head >form.fm{
	padding-left:120px !important;}
/* 导航栏 */
.s_nav >.s_tab{
	position:absolute ;
	padding:8px 0 8px 0px!important;
	background:#33373B ;
	color: white;
	width:100% ;
	left:0 ;
	top:0 ;   
}
.s_tab >a , .s_tab >b{
	margin:0;
	border-right:1px solid #555;
	padding:8px 12px 8px 0 !important;;
	color:#C0C0C0 !important;
	font-weight:bold;
	text-decoration:none !important;
}
#s_tab > a:nth-child(1){margin-left:12px;}
.s_tab >b{color:#FAFAFA !important;}
#u{padding:5px ;}
#u >#user >a ,#u >a{color:#C0C0C0 !important;}
.c-icon-triangle-down-blue{background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAECAYAAAC6Jt6KAAAAHUlEQVQImWM4cODAf1yYARcgSTE2TQQVI2sipAYAHFYz8Z+xI1kAAAAASUVORK5CYII=) no-repeat scroll 130% 50% transparent;}

/* 搜索框 */
.bg.s_ipt_wr{
	background:white !important;
	border:1px solid #CDCDCD !important;
}
.bdsug{border:1px solid #CDCDCD !important;}
.bg.s_btn{
	color:#FFF;
	font-family:"微软雅黑";
	background:#388BFF ;
}
/* 内容 */
#wrapper >#wrapper_wrapper ,#wrapper >#container{
	width:100%;
	border-top:2px solid #388BFF;
	padding-top:10px;
	margin-top:-3px;}
#con-ar,#con-ar+.result-op{
	padding:20px;
	box-shadow:0 1px 4px rgba(0,0,0,0.2)}
#con-ar+.result-op{
	position:relative;
	top:-40px}
#con-ar:empty{
	display:none}
#con-ar .cr-title>span{
	color:#222;
	font-size:16px;}
#content_left>.result-op,#page>*{
	position:relative;
	left:120px}
#content_left>:not(.result-op){margin-left:120px!important}
#con-ar{
	top:140px;
	position:absolute ;
	left:770px ;}
#content_left >*{
	border-collapse: separate !important;;
	padding-top:5px !important;}
#content_left >*:nth-child(3n) {
	padding-bottom:12px !important;
	border-bottom:1px solid #CDCDCD !important;}
.c-border{box-shadow:none;}
.extquery>a {font-size:12px !important;color:#c33 !important;}
a.m{
	font-size:12px !important;
	color:green !important;}
.icon-gw{
    color:#FFF !important;}
}
