css:
@-moz-document url("http://www.cnbeta.com/"), url("http://cnbeta.com/") {
html,body{ font-family:   'Microsoft Yahei', 'Helvetica Neue', 'Luxi Sans', 'DejaVu Sans', Tahoma, 'Hiragino Sans GB', STHeiti;}

.cb_box,.main_content_right{ display:none;}
.main_content_left section:nth-child(3){display:none;}



.all_news_wildlist dl{ width:100%;}
.all_news_wildlist .newsinfo{ width:570px;}
.all_news_wildlist .pic{ margin-right:20px;}

.items_area dt a{ font-size:20px}
.newsinfo  p{ font-size:14px !important; line-height:180%}
}

@-moz-document url-prefix("http://www.cnbeta.com/") {
html,body{ font-family:   'Microsoft Yahei', 'Helvetica Neue', 'Luxi Sans', 'DejaVu Sans', Tahoma, 'Hiragino Sans GB', STHeiti;}

#BAIDU_DUP_wrapper_689797_0,#BAIDU_DUP_wrapper_685244_0,#BAIDU_DUP_wrapper_685223_0,#BAIDU_DUP_wrapper_693405_0,#googleAd_afc,.cbv,.main_content_right{ display:none;}
.main_content_left{ width:auto !important}
.article .body{ margin:0 40px 0 40px}
.article_content .content p{ font-size:16px !important;}
#news_title{ font-size:28px !important;}
.article .body .content,.article .body .content p{ font-size:18px !important}

    
    html {
    color: rgb(0, 0, 0);
    background: #E8E8E8;
}
.time,tiny_bar,#footer{display:none;}
.rating_box{display:none;}
.navi{display:none;}
div.clearfix:nth-child(6){display:none;}
.cber{display:none;}
.copyright{display:none;}
.global_navi{display:none;}
.cb_rss{display:none;}

.wrapper {

    width: 800px;
margin:0 auto;
}

#all_news_nav > li:nth-child(2){display:none;}
#all_news_nav > li:nth-child(4){display:none;}
#all_news_nav > li:nth-child(6){display:none;}
#all_news_nav > li:nth-child(8){display:none;}

.wrapper > section:nth-child(2){display:none;}

.cnbeta_logo {
    left: -222px;
    position: absolute;
    height: 46px;
    width: 187px;
    background-position: left -163px;

}
.global_head {
    height: 10px;
    position: relative;
    z-index: 1;
}

.left_art{display:none;}

.article .body .introduction {
    border:none;
    overflow: auto;
    background: rgb(251, 251, 251);
    line-height: 28px;
    font-size: 16px;
    padding: 10px 24px 10px 18px;
    margin: 0px auto 3px;
    color: #434343;
    width:680px;
}

.blue_bar li {
    display: inline;
    font-size: 20px;
}
 .article .body .content p {
    line-height: 30px;
    margin: 8px auto 0px;
    text-indent: 2em;
}

}
