css:
@-moz-document domain("fronter.com") {
.UIComposer_InputArea * {text-shadow: none !important;}

.libs.gui-class-lib.content-frame-header.navigational{background: url("http://creativenerds.co.uk/wp-content/uploads/2011/11/redstripes.png") repeat-x !important;}

.content-tab-label-text-unselected{color:#ffffff !important; text-shadow:none!important;}

.shortcut-tab.fronterjq-tab{background: url("http://creativenerds.co.uk/wp-content/uploads/2011/11/greenstripes.png") repeat-x !important}

.shortcut-tab.fronterjq-tab.hover{background: url("http://creativenerds.co.uk/wp-content/uploads/2011/11/purplestripes.png") repeat-x !important}

.shortcut-tab.fronterjq-tab.selected{background: url("http://creativenerds.co.uk/wp-content/uploads/2011/11/redstripes.png") repeat-x !important}

.shortcut-tab.fronterjq-tab.selected.hover{background: url("http://creativenerds.co.uk/wp-content/uploads/2011/11/purplestripes.png") repeat-x !important}

.tab-link{color:#ffffff!important}

#top-row{background: url("http://pimpyourkitchen.co.uk/dynamic/media/4/images/Designs/kitchen_splashback_design_stripes.jpg")!important}

#bottom-row{background: url("http://pimpyourkitchen.co.uk/dynamic/media/4/images/Designs/kitchen_splashback_design_stripes.jpg")!important}

.sprite-title{color:#000000!important}

.dropdown-menu{background:url("http://fronter.com/volY12-cache/cache/img/design_images/Play/other_images/background.png") fixed repeat!important;}

.open-menu-button .dropdown-title-container{background:url("http://pimpyourkitchen.co.uk/dynamic/media/4/images/Designs/kitchen_splashback_design_stripes.jpg")!important;}

.open-menu-button .dropdown-title{color:#000000!important;}


div#personal_tools a{

padding-left:5px;

color:#000000;

}



td.image-button {

background-image:url("http://i34.tinypic.com/9qzkmp.png");

padding-left:5px;

}



td.text-button {

background-image:url("http://i34.tinypic.com/9qzkmp.png");

max-height:23px;

}



td.text-button-selected {

background-image:url("http://i34.tinypic.com/9qzkmp.png");

}



td.separator {

background-image:none;

width:2px;

}



hr.separator-horizontal {

border:0px;

}



td#tab_bg_0{

background-color:#ff0000;

}



td#tab_bg_0 {

background-color:#000000;

border:0px solid #ffaa00;

height:30px;

width:400px;

}



table.help-logout {

margin:0px 0px 1px 0px;

height:23px;

}



td.help-logout {

border:0px solid #1b6184;

}



td.help-logout img{

padding-left:5px;

padding-right:5px;

}



a.help-link {

color:#000000;

}



a.logout-link {

padding-right:5px;

color:#000000;

}

span.sprite_title{
background-color: rgba(0,0,0,0.5) !important;
color: white !important;
}
}
