css:
@-moz-document domain("oji3fromdti.blog.fc2.com"){
/*==========================================
  oji3学習帳 liquid compact 2014/03
  domain(oji3fromdti.blog.fc2.com)
  ==========================================*/
/*--- unwanteds ---*/
#yads,
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
div {box-sizing: border-box; -moz-box-sizing: border-box;}

/*--- page ---*/
body {margin: 0 !important;}
#outline, #outline>div {width: auto !important;}

#outline_main .pankuzu_list {margin: 0 !important;}
#outline #main-first {width: 100% !important;}
#main-first #main {width: 100% !important; margin: 0 0 0 -150pt !important;}
#main-first #main>* {width: auto !important; margin: 0 0 0 150pt !important;}
#main-first #main>* * {max-width: 100% !important;}
#main-first #first {width: 150pt !important;}
#main-first #main .ently_text>span,
#main-first #main .ently_text>strong,
#main-first #main .ently_text>a
 {display: block !important; font-size: medium !important;}
#main-first #main .ently_text br {display: none !important;}
}

@-moz-document domain("oji3.dtiblog.com"){
/*==========================================
  oji3学習帳 旧版 liquid compact 2014/03
  domain(oji3.dtiblog.com)
  ==========================================*/
/*--- unwanteds ---*/

/*--- generic containers ---*/
div {box-sizing: border-box; -moz-box-sizing: border-box;}
body {font-family: inherit !important; font-size: medium !important;}
#container {width: auto !important;}

/*--- header ---*/
#branding {width: auto !important; height: auto !important; margin: 0 !important;}
.backimg {
 width: auto !important; height: auto !important;
 margin: 0 !important; padding: 0 !important;
 }
/*--- navigation bar ---*/
#pulldown {position: static !important;}
.maindown li {width: auto !important; padding: 0 0.5em !important;}

/*--- topic ---*/
#primary-column
 {width: 100% !important; margin: 0 0 0 -150pt !important; padding: 0 !important;}
#primary-column>* {width: auto !important; margin: 0 0 0 150pt !important;}
#secondary-column {width: 150pt !important; padding: 0 0.5em 0 0 !important;}
.entry-body>span, .entry-body img {display: block !important;}
.entry-body center {text-align: left !important;}
.entry-body br {display: none !important;}
}
