@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

/* Right sidebar */
div[id^="rightCol"] {
    display: none !important;
}

/* "Related" and "Similar" blocks */
._54bx, ._54bz,
._4-u2.mbm._la {
    display: none !important;
}
    
/* Extend width of main content */
div[id^="contentArea"] {
    width: 95% !important;
}

/* Fix content moving left after FB April 12th 2017 update */
._1qkq {
    position: static !important;
}
       
/* Change font to look like old style */
body, button, input, label, select, td, textarea,
a[class^="_5v47"],
span[class^="text_exposed_link"],
.UFICommentActions,
.UFIBlingBoxRevised {
    font-family: 'lucida grande',tahoma,verdana,arial,sans-serif !important; 
    font-size: 11px !important;
}
.userContent {
    font-size: 13px !important;
}
._1k0o {
    /* Left column navigation */
    font-size: 11px !important;
}
._5pcb._dp7 ._5vsj, ._5pcb._dp7 ._5vsj .fsm, ._5pcb._dp7 ._5vsi, ._5pcb._dp7 ._5v47, ._5pcb._4j3f .UFIAddCommentInput {
    /* Number of likes, comments font size */
    font-size: 11px !important;
}
    
/* Comments readability */
/* .UFIRow {
    border-top: 1px solid #E1E2E3;
}
.UFIFirstComment {
    padding-top: 4px !important;
} */

/* Quoting readability */
._5pco {
    color: #336633 !important;
}

/* Drop shadow */
._4-u2 {
    box-shadow: 2px 2px 3px rgba(150, 162, 170, 1);
}

/* Stop nagging to install messenger app */
._s15 {
    display: none !important;
}
 
/* Decrease messenger font size */    
.__i_ {
	font-size: 13px !important;
}

}
