css:
@-moz-document domain("userstyles.org") {
/*==============================
Main
==============================*/
HTML {
  border-top:solid 2px #eee;
}

body {
 background:url(http://i.imgur.com/8O7zTrY.jpg) repeat;
  resize:none;
}

/*==============================
Links
==============================*/

a:visited:hover {
color: #335;
}

a:visited {
color: #155;
}

a:hover {
color: #113;
}

a {
  color: #151;
}

/*==============================
Header
==============================*/
#main-header > h1 > a {
  color: rgba(0, 0, 0, 0)!important;
  text-shadow: none!important;
  background: url(http://i.imgur.com/bL1td1i.png) no-repeat;
  background-color:transparent;
  font-size: 40px!important;
  letter-spacing:1px;
}

#main-header {
  color:#555;
  background:url(http://i.imgur.com/fbskWaP.jpg) repeat;
  border:dashed 1px #fff;
  text-transform:uppercase;
  text-shadow: 1px 1px 0 #EBE0D4, 2px 2px 0 #B5916D;
  box-shadow:2px 2px 0 #EBE0D4, 4px 4px 0 #B5916D;
}

/*==============================
Sidebar
==============================*/
#left-sidebar {
  font-size:11px;
  font-weight:bold;
  width:195px;
}

#left-sidebar a {
  text-decoration:none;
}

#left-sidebar ul {
padding-left: 0;
margin-top: 0;
}

#left-sidebar>* {
  background:#7a9;
  margin:10px !important;
  padding: 16px;
  border-radius:32px;
  border:solid 2px #ccc;
  text-shadow: -1px -1px 0 rgba(0,0,0,0.10), 1px 1px 0 rgba(255,255,255,0.20);
  box-shadow:2px 2px 0 #7a9, 2px -2px 0 #7a9, -2px -2px 0 #7a9, -2px 2px 0 #7a9;
}

#search-terms {
  margin-bottom:32px;
  background:#9ba;
  border:2px solid #ccc;
  border-radius:4px;
}

#show-advanced-search {
  color:#558;
  text-transform:uppercase;
  text-decoration:none;
  padding:2px 4px;
  background:#7a9;
  border:1px solid #558;
  border-radius:16px;
}

/*==============================
Content
==============================*/
#content-wrapper {
  background:url(http://i.imgur.com/sRb2fPG.jpg) repeat #377;
  box-shadow:2px 2px 0 #EBE0D4, 4px 4px 0 #B5916D;
  border-top-left-radius:8px;
  border-top:solid #B5916D 1px;
  border-left:solid #B5916D 1px;
  padding-top:10px;
  margin-bottom:18px;
  color:#444;
  text-shadow: 1px 1px 0 #EBE0D4;
  letter-spacing:0px;
  min-height:300px;
}

#front-page-best > h2, #front-page-newest > h2 {
  text-shadow: 1px 1px 0 #EBE0D4, 2px 2px 0 #B5916D;
}

#front-page-best a, #front-page-newest a {
  text-decoration:none !important;
}

#front-page-best, #front-page-newest {
  background:url(http://i.imgur.com/8O7zTrY.jpg) repeat #333;
  width:100%;
  color:#555;
  margin-bottom:16px;
  padding:10px;
  border: solid 1px #EBE0D4;
  box-shadow:inset 2px 2px 0 #B5916D;
  text-shadow: 2px 1px 0 #EBE0D4,  3px 2px 0 #EBE0D4, 4px 3px 0 #B5916D;
  line-height:25px;
}
#front-page-best > a{
text-decoration:none !important;
}

#main-article > h2 {
  text-shadow: 2px 2px 0 #B5916D !important;
}

#view-code {
  background:rgba(0,0,0,0.08);
  border-top:1px solid rgba(0,0,0,0.50);
  border-right:1px solid rgba(255,255,255,0.70);
  border-bottom:1px solid rgba(255,255,255,0.70);
  border-left:1px solid rgba(0,0,0,0.50);
  margin-right:25px;
}

/*==============================
Buttons
==============================*/
.install {
  color:#cfc;
  width:153px;
  height:40px;
    font-size:10px;
background-color: #489615;
    background:url(http://i.imgur.com/bfBUU01.png) no-repeat;
cursor: pointer;
    text-shadow:1px 1px 0 #050;
}

.install:hover {
  color:#fff;
  width:153px;
  height:40px;
    font-size:10px;    background:url(http://i.imgur.com/bfBUU01.png) no-repeat;
cursor: pointer;
    text-shadow:1px 1px 0 #050;
}

.no-install {
  color:#855;
background:transparent !important;
border:solid 1px;
  border-radius:0px;
  text-shadow:none;
}

.install-info {
  color:#585;
  background:transparent !important;
  border:solid 1px #585;
  border-radius:0px;
  text-shadow:none;
}

.install-info > a {
  color:#585;
    text-shadow: 1px 1px 0 #EBE0D4, 2px 2px 0 #B5916D;
}

/*==============================
Footer
==============================*/
footer {
  text-align:center;
  padding:10px 0px 10px 0px;
    background:url(http://i.imgur.com/fbskWaP.jpg) repeat;
  border:dashed 1px #fff;
  font-weight:bold;
  text-transform:uppercase;
  text-shadow: 1px 1px 0 #EBE0D4, 2px 2px 0 #B5916D;
  box-shadow:2px 2px 0 #EBE0D4, 4px 4px 0 #B5916D;
}

footer a {;
  text-decoration:none;
}

/*==============================
Ads
==============================*/
.ad-note {
  width:0% !important;
  height:0% !important;
  margin:-9999px !important;
  font-size:0px !important;
  opacity:0.00;
}

#google_ads_frame1, .ad, .adsbygoogle {
  width:0% !important;
  height:0% !important;
  font-size:0px !important;
  opacity:0.00;
}

#post-install-ad {
  width:0% !important;
  height:0% !important;
  font-size:0px !important;
  opacity:1.00;
}

iframe {
  width:0px;
  height:0px;
}
}
