css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.linkedin.com") 
{
/* hide ads and unused elements */
    #content > div:first-child       /* upload my contacts panel*/
    ,ul#control_gen_5 li:nth-child(3) /* upload my contacts menu */
    ,#pmyk
    ,#companiesForYou
    ,#jobsForYou
    ,#right-rail-ad
    ,#member-ads
    ,#share-stats-widget
    ,#footer
    ,#marquee
    ,#nav-utility-lang
    ,#search-promo
    ,#ad-slot-2
    ,div.ad-block
    {
        display:none!important;
        visibility:hidden!important;
    }

}
