css:
@-moz-document domain("chs5.net") {
body {
	background: #a6a6a6 url('http://i.imgur.com/y9sQqEk.png') repeat;
	color: #7284cf;
	font: normal 12px tahoma, arial, verdana, sans-serif;
	position: relative;
}

.filter_bar {
	background: #1c1c1c url('http://i.imgur.com/Prmint2.png') repeat-x 0 0;
	color: #a6a6a6;
	border: 1px solid #1b1b1b;
}

.header {
color: #e0e0e0;
}

#user_links a{
	float: left;
	padding: 0px 12px;
	line-height: 34px;
	display: block;
	outline: none;
	color: #e0e0e0;
}

#secondary_nav a {
color: #e0e0e0;
}

#secondary_nav {
color: #a6a6a6;
}

.message {
background: #353e65;
border: 1px solid #1b1b1b;
color: #a6a6a6;
  }
  
  h2.page_title, .dark_text {
color: #a6a6a6;
}

a {
color: #8c9ef7;
}

.tab_bar li.active, .tab_bar li.active a {
color: #6678bd;
}

#primary_nav {
position: relative;
background: #353e65 repeat-x 0 0;
border: 1px solid #1b1b1b;
}

#primary_nav ul li a:link, #primary_nav ul li a:visited, #primary_nav ul li a:hover, #primary_nav ul li a:active {
color: #7284cf;
}

#primary_nav ul li a:hover, #primary_nav ul li a:hover span {
color: #1f45de;
}

#primary_nav ul li.active a:link span, #primary_nav ul li.active a:visited span, #primary_nav ul li.active a:hover span, #primary_nav ul li.active a:active span {
color: #7284cf;
}

a:hover {
color: #1f45de;
}

select {
color: #1c1c1c;
background: #7284cf;
}

keygen, select, select[size="0"], select[size="1"] {
border-radius: 0px;
border-color: #1b1b1b;
}

input, input[type="password"], input[type="search"], isindex {
-webkit-appearance: textfield;
padding: 1px;
background-color: #7284cf;
  }
  
#footer_utilities {
background: #353e65 repeat-x 50%;
background-position-x: 50%;
background-position-y: 50%;
background-size: initial;
background-repeat-x: repeat;
background-repeat-y: no-repeat;
background-attachment: initial;
background-origin: initial;
background-clip: initial;
background-color: #353e65;
border-radius: 4px;
-moz-border-radius: 4px;
-webkit-border-radius: 4px;
text-shadow: #1c1c1c 0px -1px 0px;
color: #a6a6a6;
}
  
#board_footer a {
color: #a6a6a6;
}

#search input#main_search {
padding: 0;
border: 0;
width: 200px;
background: #7284cf;
  }
    
#search_wrap {
background: #7284cf;
}
      
#search_options_menucontent {
background: #7284cf;
}
  
#search .choice {
background: url('http://i.imgur.com/JovoLay.png') no-repeat right;
border-left: 1px solid #1b1b1b;
padding: 0 9px 0 7px;
color: #1c1c1c;
  }
  
#search .submit_input {
background: #7284cf url('http://i.imgur.com/E6Wx6oL.png') no-repeat 50%;
  }
  
.show_search, .hide_search {
background: #7284cf url('http://i.imgur.com/E6Wx6oL.png') no-repeat 50% 50%;
z-index: 800;
border-top: 1px solid #1b1b1b;
}
    
#quick_nav_toggle {
width: 30px;
outline: none;
height: 29px;
background: #7284cf url('http://i.imgur.com/P57NbGx.png') no-repeat 50% 50%;
text-indent: -3000em;
display: inline-block;
-moz-border-radius: 2px;
-webkit-border-radius: 2px;
border-radius: 2px;
z-index: 780;
position: relative;
border-top: 1px solid #1b1b1b;
}

.moderation_bar {
background: #e8e8e8 url('http://i.imgur.com/Prmint2.png') repeat-x 0 0;
border: 1px solid #1b1b1b;
}

ul.pagination li, ul.pagination li.total:hover {
background: #353e65 repeat-x 0 0;
font-size: 0.9em;
border: 1px solid #1b1b1b;
}

p.edit {
background-color: #353e65;
color: #a6a6a6;
}

ul.pagination a {
color: #a6a6a6
}

ul.pagination a, ul.pagination li.active, ul.pagination li.pagejump, ul.pagination li.total {
text-decoration: none;
padding: 4px 7px;
display: block;
font-weight: bold;
color: #a6a6a6;
}

p.citation {
background: #353e65 url(http://forums.chs5.net/public/style_images/engraved/citation_bg.png) no-repeat right;
color: #a6a6a6;
}

body .maintitle {
background: #353e65 repeat-x 0 0;
border: 1px solid #1b1b1b;
color: #e0e0e0;
text-shadow: #1c1c1c 0px 0px 0px;
}

body .maintitle a {
color: #a6a6a6;
}

.post_block h3 {
background: #353e65 repeat-x 0 0;
border-top: 1px solid #1b1b1b;
border-bottom: 1px solid #1b1b1b;
border-right: 1px solid #1b1b1b;
border-left: 1px solid #1b1b1b;
color: #1c1c1c;
text-shadow: #7d7d7d 0px 1px 0px;
}

.post_block h3 a {
color: #a6a6a6;
}

.post_date {
color: #a6a6a6;
font-size: 11px;
}

.topic_buttons li a {
background: #353e65 no-repeat top right;
padding: 9px 9px 9px 5px;
padding-top: 9px;
padding-right: 9px;
padding-bottom: 9px;
padding-left: 5px;
border: 1px solid #1b1b1b;
}

.topic_buttons li a, .topic_buttons li span {
text-decoration: none;
color: #a6a6a6;
text-shadow: #1c1c1c 0px 1px 0px;
font-size: 0.9em;
display: block;
margin: auto 0px auto 0px;
outline: 0;
}

.post_block {
background: #1f1f1f repeat;
}

div.blockquote {
background: #2e2e2e;
}

ul.post_controls a {
background: #353e65 no-repeat top right;
color: #a6a6a6;
text-shadow: #1c1c1c 0px 1px 0px;
font-weight: bold;
text-decoration: none;
padding: 5px 9px 5px 4px;
margin-left: 0px;
display: block;
border: 1px solid #1b1b1b;
}

ul.topic_jump li a {
color: #a6a6a6;
background: #353e65 repeat-x 0 50%;
padding: 3px 6px;
line-height: 200%;
border-radius: 3px;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
text-decoration: none;
border: 1px solid #1b1b1b;
}

.input_submit {
background-color: #353e65;
color: #a6a6a6;
border: 1px solid #1b1b1b;
text-shadow: #1c1c1c 0px 1px 0px;
}

.input_submit.alt {
background-color: #353e65;
border: 1px solid #1b1b1b;
color: #a6a6a6;
text-shadow: #1c1c1c 0px -1px 0px;
}

a.altlink {
color: #8c9ef7;
hover: #6678bd;
}

.bar {
color: #e0e0e0;
}
  
.input_text {
background: #7284cf;
color: #1b1b1b;
}

.message.unspecific {
color: #e0e0e0;
}

.maintitle .desc {
font-size: 0.8em;
color: #a6a6a6;
}

.quick_text, .quick_password {
border: 0;
border-radius: 3px;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
padding: 6px;
color: #252525;
font-size: 14px;
font-family: "Trebuchet MS", Arial, Helvetica, sans-serif;
padding-left: 26px;
width: 200px;
background: #7284cf url("http://forums.chs5.net/public/style_images/engraved/ql_username.png") no-repeat 5px 50%;
}

.ql_1 {
font-size: 20px;
font-weight: bold;
color: #e0e0e0;
font-family: "Myriad Pro", "Trebuchet MS", Arial, Helvetica, sans-serif;
height: 35px;
line-height: 35px;
}

a.full_signin {
float: right;
outline: none;
color: #e0e0e0;
font-weight: normal;
text-decoration: none;
line-height: 35px;
font-size: 11px;
}

#user_navigation ul#user_link_menucontent {
background: #7284cf;
border: 1px solid #333333;
-webkit-box-shadow: rgba(0, 0, 0, 0.58) 0px 12px 25px;
-moz-box-shadow: rgba(0, 0, 0, 0.58) 0px 12px 25px;
box-shadow: rgba(0, 0, 0, 0.58) 0px 12px 25px;
margin-top: 1px;
width: 170px;
float: right;
z-index: 10000;
}

#user_navigation ul#user_link_menucontent a {
color: #333333;
text-decoration: none;
border-bottom: 1px solid #1b1b1b;
padding: 8px 5px 8px 15px;
display: block;
}

#user_notifications_link_menucontent li.notify {
background: #7284cf;
}

#user_notifications_link_menucontent li {
padding: 5px 10px 5px 8px;
float: none;
clear: both;
border-bottom: 1px solid #1b1b1b;
}

#user_notifications_link_menucontent {
color: #e0e0e0;
}

#user_notifications_link_menucontent a {
color: #1c1c1c;
}

#main_search_form #main_search {
color: #e0e0e0;
font-size: 17px;
}

#main_search_form .search_app.active {
background: #373737;
border: 1px solid #1b1b1b;
color: #7284cf;
border-radius: 3px;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
}

.sidebar_toggle {
background-color: #353e65;
position: absolute;
right: -13px;
top: 3px;
}

.topic_buttons li.disabled span, .topic_buttons li.disabled a {
background: #2f2f2f;
color: #e0e0e0;
text-shadow: #e2e2e2 0px 0px 0px;
border: 1px solid #e0e0e0;
}
}
