css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(https://duckduckgo.com){
font,
.icon_fav2,
#side > div:not(#side_suggestions),
#side_suggestions .spacer_bottom_7,
#feedback_wrapper{
display: none!important;
}

#zero_click,
.snippet,
.links_main,
#side-wrapper,
[class^='results']{
display: inline-block!important;
}

#side_suggestions{
padding:0!important;
}

[class$='suggestion']{
width:auto!important;
margin-bottom: 0!important;
}

#side{
width: 155px!important;
position: fixed!important;
right: 0px!important;
top: 0px!important;
height: 57px;
}

#side,
#content_wrapper{
background-attachment: fixed!important;
background-color: transparent!important;
background: url("https://duckduckgo.com/assets/header_bg.v102.png") repeat-x scroll 0 0 rgba(0, 0, 0, 0);
}

#header_wrapper{
width: calc(100% - 155px)!important;
z-index: 2!important;
}

#content_wrapper{
z-index: 1!important;
}

[class$='suggestion'] a{
color: #ccc!important;
height: auto!important;
width: auto!important;
}

[class$='suggestion'] a:hover{
color: #fff!important;
}

body{
line-height: 110%!important;
}

#links,
[id$='wrapper'],
#links div,
[id^='content'],
[class^='results']
[class$='suggestion']{
padding-left: 0!important;
padding-right: 0!important;
max-width: none!important;
line-height:inherit!important;
}

[id^='zero_click']{
overflow-x: hidden!important;
overflow-y: hidden!important;
}

[class^='results']{
float: left!important;
min-height: 110px!important;
}

#content{
padding-top:60px!important;
}

#links,
[id^='content']{
width: 100%!important;
}

#zero_click_wrapper,
[class^='results'],
.links_main{
margin: 0 4px!important;
}

[class^='results']{
padding-bottom: 0!important;
width: calc(50% - 12px)!important;
}

#zero_click,
.snippet,
.links_main{
width: calc(100% - 20px)!important;
}
}
