css:
@-moz-document domain("podio.com") {
/*
.items-card>.scrollable table tbody .card-group-body td .sortable-column {
	position: absolute;
	width: 85%;
	height: 99%;}


.ui-state-highlight {
  position: absolute;
width: 85%;
height: 99%;}
*/


/* CONTACT AND APP ITEM BUTTONS */
    .value.field-type-contact .button-new {
      height: 63px !important;
      line-height: 64px !important;
      padding-top: 0 !important;
      padding-bottom: 0 !important;
      padding-left: 3px !important;
      padding-right: 3px !important;
      font-size: 0 !important;
      border-radius: 5px !important;}

/* CONTACT AND APP ITEMS */
.field-type-contact .summary-badge-list .summary-badge .subline,
.field-type-contact .summary-badge-list .summary-badge .title {display:none !important;}
.field-type-contact .summary-badge-list .summary-badge {
    display: inline-block !important;
    float: left;
    margin-right: 20px;}
    .field-type-contact .summary-badge-list .summary-badge .image-block .bd {padding-top:10px;}
.field-type-contact .summary-badge-list .summary-badge.summary-badge-grey .inner {padding: 10px 10px 7px 10px !important;}

.summary-badge-list .summary-badge.summary-badge-grey a.action-dropdown {
  font-size: 0 !important;
  padding: 0px !important;
  top: -4px !important;
  right: 0 !important;
  border: none !important;}
  


.balloon.app-box-supermenu {top: 36px !important; left: -66px !important;}



/* TURN OFF LOADING ANIMATIONS 
    #space-navigation, .app-wrapper, .app-view-sidebar, .space-switcher-wrapper, .content-container, .space-switcher .search .default-title.with-space, body, .app-view-header-area, .app-view-header, .conversation-container .events-container ul.events>li>div,  .right-nav .show-conversation-container .events-container ul.events>li>div,  .sidebar-chat .show-conversation-container .events-container ul.events>li>div, .delayed-content {
      -webkit-transition-duration: 0s !important;
      transition-duration: 0s !important;
      -webkit-transition:none !important;
      transition:none !important;
      opacity:1 !important;
      display:block !important;}*/
/* MAKE ANIMATIONS FASTER */    
    .right-nav.chat-pane {-webkit-transition-duration: 0.5s !important;}
    .space-switcher .space-switcher-sidebar.visible {
      -webkit-transition-timing-function: cubic-bezier(0.860, 0.000, 0.070, 1.000); 
      transition-timing-function: cubic-bezier(0.860, 0.000, 0.070, 1.000); /* easeInOutQuint */}

/* FULL WIDTH */
    .space.apps .page-width {width: 100% !important; min-width:100% !important; max-width:4000px !important;}

/* SMALLER SIDEBAR */
    .space.apps .grid-column-3-4 {width: 83% !important; padding: 0 !important; margin: 0 !important;}
    .space.apps .grid-column-1-4 {width: 17% !important; padding: 0 !important; margin: 0 !important;}
    .space.apps .grid {padding-left:.75% !important; padding-right:.75% !important;}
    .space.apps .grid .grid-column-1:last-of-type, 
    .space.apps .grid .grid-column-1-2:last-of-type, 
    .space.apps .grid .grid-column-1-3:last-of-type, 
    .space.apps .grid .grid-column-2-3:last-of-type, 
    .space.apps .grid .grid-column-1-4:last-of-type, 
    .space.apps .grid .grid-column-3-4:last-of-type {padding-right: .75% !important;}
    .space.apps .app-view-sidebar .grid-tile {padding: 3% !important;}

/* Remove Useless Things */
	.space.apps .items-card .navigation-arrow, 
	.space.apps .action-bar-wrapper, 
	.space.apps #app_box .description, 
	.space.apps #app_box .usage {display:none !important;}
 
/* Less padding and margin */
    .space.apps .items-card {margin-right:0 !important;}
    .space.apps .grid-tile.app-wrapper {padding-left:0px !important;}
    .space.apps .app-view-main .app-view-header-area {margin-right: 0 !important;}
    .space.apps .app-view-header.v2>ul>li.title {margin-left: 15px !important;}
    .space.apps .items-card>.scrollable table tbody .card-group-body td .sortable-column {min-height: 15px !important;}
    /* .space.apps .items-card>.scrollable table tbody .card-group-body td .sortable-column .card-item .footer {
        height: auto !important;
        padding: 1px !important;
        padding-left: 3px !important;} 
    .space.apps .items-card>.scrollable table tbody .card-group-body td .sortable-column .card-item .body {
        padding: 6px !important;
        padding-top: 4px !important;
        padding-bottom: 0 !important;
        border: 2px solid #e9e9e9 !important;} */
    .space.apps #app_box .header {min-height: 38px !important;}




/* DISABLE TASK WIDGET */
	.space.apps .grid-tile.tasks-tile {display:none !important;}

/* MAKING THE FILTER BOX BIGGER */
	.space.apps .app-filters-popover {height:750px !important;}


/* MOVING THE TASK CREATION INT MAIN HEADER AND MAKING SIDEBAR HOVER PULL OUT */
    .space.apps .grid-column-3-4 {width: 100% !important;}
    .space.apps .grid-column-1-4 .app-widgets {
        -webkit-transform: translateZ(0);
        margin: 0 !important;
        position: fixed;
        right: -25.6%;
        width: 27% !important;
        z-index: 9;
        background-color: #5092BD;
        padding: 15px !important;
        border: none !important;
        border-left: 1px solid #F7F7F7 !important;
        border-bottom: 1px solid #F7F7F7 !important;
        border-top: 1px solid #F7F7F7 !important;
        border-top-left-radius: 3px !important;
        border-bottom-left-radius: 4px !important;
        margin-top: -13px !important;
        -webkit-transition: all 500ms cubic-bezier(0.770, 0.000, 0.175, 1.000) !important;
        top: 92px;
  		box-sizing: border-box;
  		max-height: 1%;
		overflow: hidden;
		right: -25.6%;
  		opacity:.3 !important;}
  	.space.apps .grid-column-1-4 .app-widgets .widgets-box {height:0px !important; overflow:hidden !important;}
  
    .space.apps .widgets-box .widget-admin {color: #fff !important;}
    .space.apps .widgets-box .widget-content th {font-size: 1.5rem !important; font-weight: bold !important;}
    .space.apps .widgets-box .widget-content th .unit {font-size: 1.2rem !important;}
    .space.apps .widgets-box .widget-content td {font-size: 1.3rem !important;}
    .space.apps .widgets-box .widget-content td .unit {font-size: 1.1rem !important;}
    .space.apps .widgets-box .widget-inner h3 {font-size:1.9rem !important; margin-bottom: 8px; padding-bottom: 0px;}
    
    .space.apps .app-view-sidebar .grid-tile {
        -webkit-box-shadow: 0px 1px 1px #336281;
        box-shadow: 0px 0px 2px #2A5572;}
    
    .space.apps .grid-column-1-4 .app-widgets:hover {right: 0 !important; max-height:inherit; opacity:1 !important;}
    .space.apps .grid-column-1-4 .app-widgets:hover .widgets-box {height:inherit !important; overflow:inherit !important;}
    .space.apps .grid-column-1-4 .app-widgets:hover + .grid-tile.app-box-tile {
        right: 8% !important;
        min-width: 38%;
        max-width: 38%;
        opacity:0 !important;}
    
    
    .space.apps .app-view-sidebar .grid-tile.app-box-tile {
        position: absolute !important;
        right: 3% !important;
        display: inline-block !important;
        opacity: 1 !important;
        top: 123px !important;
        background-color: transparent !important;
        border: none !important;
        box-shadow: none !important;
        padding: 0 !important;
        margin: 0 !important;
        -webkit-transition: all 500ms cubic-bezier(0.770, 0.000, 0.175, 1.000);}  
            
    .space.apps #app_box {
        margin: 0 !important;
        border-radius: 0 !important;}
    
    .app-view-header.v2 {margin-right: 210px !important;}
    
    .space.apps #app_box .header .title {font-size: 1.5rem !important;}
    .space.apps #app_box .app-icon-32 {display:none !important; margin-top: 5px !important;}
    .space.apps .util-button-new {padding: 2px !important; margin-top: 6px !important;}
    .space.apps #app_box .header .bd {padding:0 !important;}
    .space.apps #app_box a.add-item {
        padding: 0px 6px 0px 3px !important;
        font-size: 1.2rem !important;}
    .space.apps .button-with-icon .icon-16 {padding-right:2px !important;}
    .space.apps #app_box .header {min-height:33px !important;}




.items-card>.scrollable table tbody .card-group-body td .sortable-column .card-item .footer .card-tasks, 
.items-card>.scrollable table tbody .card-group-body td .sortable-column .card-item .footer .card-comments {display: none;}


/* REMOVING TASKS SECTION */
.preview-tasks {display:none !important;}


/* REMOVING FOOTER */
.footer-global {display:none !important;}


.sidebar-widget.item-tasks {display:none !important;}


/* .composer-extras,
.composer-extras .action-box {display:block !important;} */
/*.file-finder-wrapper {display:none;}*/


#wrapper .item-container .fields .field .value.field-type-largeText h3 {
  font-size: 1.85rem !important;
  margin-bottom: 6px !important;
  text-shadow: rgba(255, 255, 255, 0.85) 0px 1px 0px, rgba(255, 255, 255, 0.29) 0px 2px 0px, rgba(240, 240, 240, 1) 0px 1px 3px, rgba(0, 0, 0, 0) 0px 1px 3px, rgba(0, 0, 0, 0.07) 0px 3px 4px !important;
  color: #5092BD !important;}





.itemheader-head-abelltech .label,
.itemheader-end-abelltech {display:none;}
#wrapper .item-container .fields .field.itemheader-head-abelltech .value.field-type-category .answer.current  {
  background: none !important;
  border: none !important;
  margin: 0 !important;
  margin-top: 18px !important;
  font-family: "Raleway",sans-serif !important;
  font-size: 40px !important;
  text-shadow: 
            rgba(255, 255, 255, 0.85) 0px 1px 0px, 
            rgba(255, 255, 255, 0.29) 0px 2px 0px, 
            rgba(240, 240, 240, 1) 0px 1px 3px, 
            rgba(0, 0, 0, 0) 0px 1px 3px, 
            rgba(0, 0, 0, 0.07) 0px 3px 4px !important;
  color: #5092BD !important;
  font-weight: 700 !important;
  cursor:inherit !important;
  pointer-events: none;}


/* BADGE VIEW .items-list.badge */
	
	.items-list.badge {padding-left: 25px !important;}
    .items-list.badge .item {width: 25% !important; padding: 0 12px 12px 0 !important;}
    
    
	.items-list.badge .item .inner {
    	height: 138px !important;
        border: 3px solid #D8D8D8;
		border-radius: 3px !important;}

		.items-list.badge .item .inner .header {
  			line-height: 20px !important; 
            padding: 4px 6px 4px 6px !important;
			border: none !important;
  			border-bottom: 1px solid #D8D8D8 !important;
			border-radius: 0 !important;}

			.items-list.badge .item .inner .header .comment-count {display:none !important;}

			.items-list.badge .item .inner .header h2 {
  				margin: 0 0px 0 0 !important;
  				white-space: normal !important;
  				font-size: 1.5rem !important;}

		.items-list.badge .item .inner .footer {display:none !important;}

		.items-list.badge .item .inner .content {height:400px !important;}
		.items-list.badge .item .inner .content .middle p:first-child,
		.items-list.badge .item .inner .content .middle .label {display:none !important;}
		.items-list.badge .item .inner .content .middle p {margin:0 !important;}        

		 .items-list.badge .item .inner .content .middle {
			overflow: hidden;
			height: 400px;} 
		/*
		.items-list.badge .item .inner .content .middle p:nth-child(2) .value:before {
			float: left;
			width: 5px; height: 250px; }

		.items-list.badge .item .inner .content .middle p:nth-child(2) .value {
			float: right;
			width: 100%;
			margin-left: -5px; }*/	



/*
.headersection-abelltech {
width: 48%;

}

.grid-column-3-4 {
width: 100%;
}
.grid-column-1-4 {display:none;}
*/


/*.items-table table.data colgroup col {width:auto !important;}*/


.items-table table.data {table-layout:auto !important;  width: auto !important;}

.items-table table.data colgroup col {display:inline !important;}
/* .items-table .scrollable {top: -21px !important;} */
.table-header-draghandle {display:none !important;}




/* RESTYLING CARD LAYOUT */
    .space.apps .items-card>.scrollable table tbody .card-group-body td .sortable-column .card-item .footer {
        height: 0 !important;
        width: 0 !important;
        margin: 0 !important;
        padding: 0 !important;
        border: 0 !important;}



li strike, li del {color: #9E9E9E !important;}
          
    .space.apps .items-card>.scrollable table tbody .card-group-body td .sortable-column .card-item .footer .field-type-contact {
        visibility: visible;
        position: absolute !important;
        top: -7px !important;
        right: -5px !important;}
        .space.apps .items-card>.scrollable table tbody .card-group-body td .sortable-column .card-item .footer .field-type-contact li img {border-radius: 6px !important;}    
    
    .space.apps .items-card>.scrollable table tbody .card-group-body > td,
    .space.apps .items-card>.scrollable table tbody .card-group-body td .sortable-column {padding: 0 !important;}
    
    .space.apps .items-card>.scrollable table tbody .card-group-body td .sortable-column .card-item {
        -webkit-transform: translateZ(0);
        position: relative;
        padding: 0px !important;
        margin: 0px !important;
        margin-bottom: 11px !important;
        margin-top: 8px !important;
        border-radius: 0px !important;
        box-shadow: 0 0px 1px rgba(0, 0, 0, 1), 
      				0 0 40px rgba(0, 0, 0, 0.19) inset;
        box-sizing: border-box;
        z-index: 1;
        max-height: 37px; 
        -webkit-transition: max-height 500ms cubic-bezier(0.770, 0.000, 0.175, 1.000);
        margin-right: 8px !important;
        margin-left: 8px !important;}
      .space.apps .items-card>.scrollable table tbody .card-group-body td .sortable-column .card-item .body:first-line {font-weight:bold;}
      
    .space.apps .items-card>.scrollable table tbody .card-group-body td .sortable-column .card-item:hover {max-height: 500px !important;}
    .space.apps .items-card>.scrollable table tbody .card-group-body td .sortable-column .card-item:hover .body {max-height: 500px !important;}
    
    .space.apps .card-item:before, 
    .space.apps .card-item:after{
        content:"";
        position:absolute; 
        z-index:-1;
        box-shadow:0 0 5px rgba(0,0,0,0.4);
        top:0;
        bottom:0;
        left:10px;
        right:10px;
        -moz-border-radius:100px / 10px;
        border-radius:100px / 10px;} 
    .space.apps .card-item:after{
        right:10px; 
        left:auto;
        -webkit-transform:skew(8deg) rotate(3deg); 
           -moz-transform:skew(8deg) rotate(3deg);     
            -ms-transform:skew(8deg) rotate(3deg);     
             -o-transform:skew(8deg) rotate(3deg); 
                transform:skew(8deg) rotate(3deg);}
    
    .space.apps .items-card>.scrollable table tbody .card-group-body td .sortable-column .card-item .body {
      background: transparent !important;
      margin: 0 !important; 
      padding: 4px !important;
      padding-top: 3px !important;
      padding-bottom: 2px !important;
      border:none !important;
      border-radius:0 !important;
      color: #677278 !important;
      line-height: 116% !important;
      text-shadow: 1px 1px 1px rgba(255, 255, 255, 0.8) !important;
      word-spacing: 0px !important;
      max-height: 30px; 
      -webkit-transition: max-height 500ms cubic-bezier(0.770, 0.000, 0.175, 1.000); 
      text-overflow: ellipsis;
      -webkit-line-clamp: 2;
      overflow: hidden !important;
      -webkit-box-orient: vertical;
      background-color: #fbfcfe !important;
      background-image: -webkit-gradient(linear, left top, left bottom, from(rgb(251, 252, 254)), to(rgb(228, 235, 241))) !important;
      background-image: -webkit-linear-gradient(top, rgb(251, 252, 254), rgb(228, 235, 241)) !important;}
    
    .space.apps .items-card>.scrollable table tbody .card-group-body td {background-color: #FAFAFA !important; max-width: 200px; word-wrap: break-word;}
    .space.apps .header-col, 
    .space.apps .items-card>.scrollable .floating .floating-scrollable .inner>div, 
    .space.apps .items-card>.scrollable table thead tr th {
        width: inherit;
        padding-right: 0 !important;
        padding-left: 0 !important;}
}
