css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sto-forum.perfectworld.com") {
 .PW-postbit-content {width: auto !important; float:none !important;}
 .PW-postbit-content div.topic {width:auto !important;}
 .PW-postbit-userinfo div.ip {margin:0px !important;  float:none !important;}
 .PW-postbit-userinfo div.infraction {margin:0px !important;  float:none !important;}
 .PW-postbit-userinfo div.avatar {width:100%; text-align:center; float:none !important;}
 .PW-postbit-message {clear:none !important;}
 p.fineprint {width:auto !important;}
 div.bg-logos {width:auto !important;}
 .footer .content {width:auto !important;}
 .contentwrap {width:auto !important; }
 .PW-postbit-userinfo {width:auto !important;}
 #topbar-container ul.topWrap {padding:5px !important; max-width:100%;}

@media (max-width: 877px), (orientation:portrait)  {
 .PW-postbit-userinfo {max-width:120px !important; padding: 5px !important;}
 .PW-postbit-content {margin-left:130px;}
 .PW-postbit-userinfo .smallfont {padding-left:2px !important; padding-right:2px !important;}
 .PW-postbit-userinfo .bigusername {padding-left:2px !important; padding-right:2px !important; top:-15px;}
 .contentwrap {min-width:480px; max-width:877px !important;}
}
@media (min-width: 877px) and (orientation:landscape) {
 .PW-postbit-userinfo {width:180px !important;}
 .PW-postbit-content {margin-left:210px;}
 .contentwrap {max-width:977px;}
}

.interaction {display:none;}

}
