css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("inoreader.com") {

    #no_more_div {
        line-height: 20px!important;
        height: 20px!important;
    }

    .article_tile {
        padding: 0px;
        margin: 10px 0.5% 0px;
    }

    .dashboard_gadgets {
        margin: 5px 0px 15px 9px;
        font-size: 13px;
    }

    table.tbl td {
        padding: 2px;
        font-size: 12px;
    }

    .dashboard_gadgets .content {
        padding: 0px;
        margin-bottom: 0px;
    }

    .drag-handle {
        width: 100%;
        cursor: move;
        font-size: 15px;
        text-align: center;
        padding: 2px;
        text-transform: uppercase;
        background-color: lightgreen;
        margin-bottom: 10px;
    }

    .gadget_overview_feed {
        margin: 5px 0px 5px 0px;
        font-size: 13px;
    }

    .gadget_overview_feed_title, .log_title {
        font-size: 15px;
        font-weight: bold;
        color: #296BCC;
        text-align: center;
        padding: 2px;
        text-transform: uppercase;
        background-color: #D6DCE5;
        margin-bottom: 10px;
    }

    .gadget_overview_article {
        margin: 0px;
    }

    .parent_div {
        text-transform: uppercase;
    }

    .gadget_overview_see_more {
        font-size: 1em;
        text-align: right;
        font-style: italic;
    }

    .dashboard_gadgets .header_name {
        line-height: 16px;
    }

    .dashboard_gadgets .header_control {
        padding: 0px 5px 0px 0px;
    }

    #subscriptions_nav {
        padding-top: 0px;
    }

    .dashboard_gadgets {
        padding-top: 0px;
    }

    .empty_field {
        padding: 0px 0px 0px;
    }

    #sb_rp_notifications_button {
        display: none;
    }

    .radius {
        border-radius: 8px;
    }

    .article_tile_content_wraper.no_pic {
        margin-top: 5px;
        height: 305px;
    }

    .article_tile_title {
        font-size: 16px;
    }
}
