css:
@-moz-document domain("fronter.com") {
#top-row{
  background: rgba(255,255,255,0.16) !important;
}
#bottom-row{
  background: rgba(255,255,255,0.16) !important;
}
#top-frame{
  background: url("http://whiterabbit.net16.net/matrix-animated-image.gif");
}
body{
  background: url("http://whiterabbit.net16.net/matrix-animated-image.gif") fixed;
}
.content-tab-label-text-unselected{
  color: #ffffff !important;
}
.shadow, table.simple-content-frame, table.seperated-tabs tr.content-container td.content-container{
  background: rgba(255,255,255,0.64) !important;
}
.shortcut-tab.fronterjq-tab.selected{
  background: #00DD00 !important;
}
.shortcut-tab.fronterjq-tab.selected.hover{
  background: #00FD00 !important;
              }
.shortcut-tab.fronterjq-tab{
  background: #005500 !important;
}
.shortcut-tab.fronterjq-tab.hover{
  background: #008800 !important;
}
legend{
  background: rgba(255,255,255,0) !important;
}
.tool-button:hover, .tool-button:focus{
  background-color: #347B27;
}
.open-menu-button{
  background: -webkit-linear-gradient(#00EE00, #00AA00); /* For Safari 5.1 to 6.0 */
  background: -o-linear-gradient(#00EE00, #00AA00); /* For Opera 11.1 to 12.0 */
  background: -moz-linear-gradient(#00EE00, #00AA00); /* For Firefox 3.6 to 15 */
  background: linear-gradient(#00EE00, #00AA00); /* Standard syntax */
}
.dropdown-menu{
  background: -webkit-linear-gradient(#004400, #005000, #004400);
  background: -o-linear-gradient(#004400, #005000, #004400);
  background: -moz-linear-gradient(#004400, #005000, #004400);
  background: linear-gradient(#004400, #005000, #004400);
}
.navbar.body.expanded{
  background: rgba(255,255,255,0.64) !important;
}
.links.top-frame.body.trail.navigational.ltr{
  background: rgba(255,255,255,0.64) !important;
}
#navbar-minimise-button-wrapper{
  background: none !important;
}
.room-tool-container.selected{
  background: rgba(0,255,0,0.3) !important;
  border-radius: 5px !important;
}
.room-tool-container.selected:hover{
  background: rgba(0,255,0,0.48) !important;
  border-radius: 5px !important;
}
.room-tool-container:hover{
  background: rgba(255,255,255,0.3) !important;
  border-radius: 5px !important;
}
#navbar-minimise-button{
  background: -webkit-linear-gradient(White, LightGreen); /* For Safari 5.1 to 6.0 */
  background: -o-linear-gradient(White, LightGreen); /* For Opera 11.1 to 12.0 */
  background: -moz-linear-gradient(White, LightGreen); /* For Firefox 3.6 to 15 */
  background: linear-gradient(White, LightGreen); /* Standard syntax */
  border-radius: 4px !important;
}
#navbar-minimise-button:hover{
  background: -webkit-linear-gradient(LightGreen, White); /* For Safari 5.1 to 6.0 */
  background: -o-linear-gradient(LightGreen, White); /* For Opera 11.1 to 12.0 */
  background: -moz-linear-gradient(LightGreen, White); /* For Firefox 3.6 to 15 */
  background: linear-gradient(LightGreen, White); /* Standard syntax */
  border-radius: 4px !important;
}
.room-tool-list{
  border-bottom: none !important;
}
.body.personal .old-news-element .content-tab-label{
  background-image: url("http://fronter.com/volY12-cache/cache/img/design_images/Play/icons/today_icons/news.png") !important;
}
body.personal .room-list-element .content-tab-label{
  background-image: url("http://fronter.com/volY12-cache/cache/img/design_images/Play/icons/today_icons/entrance_hall.png") !important;
}
#logo-image:hover{
  background-image: url("http://www.nitrocomics.com/image/38854170.jpg") !important;
  background-size: 100%;
  width: 150px !important;
  height: 40px !important;
}
.container-inner > p > a > span{
  color: black !important;
}
}
