css:
@-moz-document domain("fxp.co.il") {
* { font-family: Arial, Helvetica, sans-serif !important; }

.greengr, .blockhead, .thread_controls:not(.graygr) { background: #FFF !important;  }
.bluegr, .ftitle, .threadlisthead { background: #FAFAFA;  color: #666666; }
.cat_title { color: #666666; }
.fixrd {border-top-left-radius: 10px;border-top-right-radius: 10px;}
#thread_controls:not(.graygr), table.pagetitle, div.threadlisthead, ol#stickies > li, ol#threads > li, .flo, .leftbl, .toplogin, .tcat, .graygr:not(#thread_controls), .newcontent_textcontrol, .userinfo_noavatar, .textcontrols { box-shadow: 0 0 3px #7F8C8D;}
form.pagination > span > a:not(.popupctrl), #thread_controls.graygr, .titleshowt, .postbody, .posthead-date, #topmenunew > .sec, #topmenunew > .first { box-shadow: 0 0 1px #7F8C8D; }
div.wysiwyg_block > div.blockbody, div.wysiwyg_block > div.blockbody > div.blockrow:last-child {border-bottom-left-radius: 5px; border-bottom-right-radius: 5px;}
a.cat_title:hover { color: #666666; opacity: 0.8; }
div.ct.c-fav {display: none;}
.fav_div { border-right: none; border-top-right-radius: 10px; border-bottom-right-radius: 10px;  }
#topmenunew .first, #usercp_nav .blockrow .active { background-color: #8abddf; }
#usercp_nav .blockrow * a:hover  { background-color: #CCE1F1 !important; }
.gu ul, ol.childforum { padding-top: 1px; }

.cats { margin-top: 20px; padding-bottom: 40px; }
li#cat4 { padding-bottom: 5px; margin-top: 10px; }
.blockfoot { padding-bottom: 20px; }

div[style="float: right;margin-top: 7px;"] { float: left !important; }

li.forumbit_post.L1 table{ border-bottom: 1px solid #c3c3c3; }

.posthead-date { background: #157cc0 !important; color: white !important; }

div.leftbl.flo { padding-top: 5px; }

.popupbody, iframe[src="http://images.fxp.co.il/mako/?rss=1"] { border-radius: 5px; }

.blockfoot { padding-bottom: 0; }

.sortrow { background: #f6f6f6; }

ol.childforum li:first-child { margin-top: 2px !important; }
.thread_controls.graygr { padding: 3px 0; }

#inlinemod_formctrls {right: 20px; top: 60px; } 
li#moderationtools { right: 20px; top: 20px; background: url('http://fcdn.co.il/images_new/gradients/generic_button.png') repeat-x scroll left top #E9E9E9; width: 100px; text-align: center; padding: 3.33333px 10px 3.33333px 5px; border: 1px solid #A8A8A8; }
li#moderationtools h6 a { font: bold 11px Arial,Calibri,Verdana,Geneva,sans-serif !important; }
li#moderationtools, #inlinemod_formctrls { position: fixed;  }
.popupbody { left: -125px !important; }
    
/*We don't wanna see this..*/
#slide, #maybe, #adfxp, .related_right, .related_ynet, .tower_right, .tower_left, .tower_left_adse, .dfxp, .fbcc, div[style="float: right;width: 200px;margin-left: 10px;  margin-top: 33px; "], div[style="float: left; margin-top: -30px"], label[for="cb_openclose"] span, .ynetdiv, li.forumbit_post.L1 div, img[src="http://images.fxp.co.il/images3/forum_new.gif"], img[src="http://images.fxp.co.il/images3/forum_old.gif"], iframe[src*="http://www.zap.co.il/"], #vc_widget, div[style="padding-right: 5px;"], #footer_morecopyright, .t, #navbar_notice_20, .flo > iframe
{
  display: none !important;
}

/*Just so you'll know...*/
#footer_copyright:before
{
  content: "modernFxp v1.13 | ";
}
}
