css:
@-moz-document url-prefix("http://radiko.jp/") {






/* ヘッダ
------------------------------- */

#globalheader-cntl {
    padding-bottom: 106px !important;
}
#globalheader {
    border: solid 1px #ccc !important;
    border-top: none !important;
    width: 998px !important;
    height: 102px !important;
    border-radius: 0 0 5px 5px !important;
}
#telop {display: none !important;}
#globalheader .hbnr {
    position: absolute !important;
    top: 19px !important;
    right: 0 !important;
    border: solid 1px #fff !important;
}
#toolbar {padding-left: 6px !important;}



/* ロゴ、エリア */
#globalheader h1 {
    top: 39px !important;
    left: 10px !important;
}
#globalheader #areatitle {
    top: 53px !important;
    left: 110px !important;
    text-align: center !important;
}
#area {width: 117px !important;}



/* 検索部分 */
#search {
    position: absolute !important;
    top: 35px !important;
    left: 475px !important;
    width: 52px !important;
    height: 46px !important;
    transition: width 0.3s ease 3s !important;
    background: rgba(0, 0, 0, 0.73) !important;
    border-left: solid 1px #fff !important;
    border-right: solid 1px #fff !important;
    z-index: 1;
    overflow: hidden;
}
#search:hover {
    transition: width 0.3s ease 0.5s !important;
    width: 240px !important;
}
#globalheader .search #search_text {
    top: -2px !important;
    left: -175px !important;
    opacity: 0 !important;
    transition: left 0.3s ease 3s, opacity 0.3s ease 3s !important;
}
#globalheader .search #search_btn {
    top: 8px !important;
    left: 8px !important;
    border-radius: 0 5px 5px 0 !important;
    transition: left 0.3s ease 3s !important;
}
#globalheader .search:hover #search_text {
    transition: left 0.3s ease 0.5s, opacity 0.3s ease 0.5s !important;
    left: 10px !important;
    opacity: 1 !important;
}
#globalheader .search:hover #search_btn {
    transition: left 0.3s ease 0.5s !important;
    left: 191px !important;
}




/* プレイヤー */
#globalheader #new_player {
    position: absolute !important;
    top: 35px !important;
    left: 236px !important;
    height: 46px !important;
    border-radius: 0 !important;
    border-width: 0 !important;
}
#globalheader #player {
    padding-top: 6px !important;
    padding-left: 4px !important;
    height: 40px !important;
    border-right: none !important;
}
#station_name {
    display: none !important;
    position: absolute !important;
    top: 0 !important;
    left: auto !important;
    right: 236px !important;
    max-width: 95px !important;
    height: 61px !important;
    padding-left: 7px !important;
    word-wrap: normal !important;
    white-space: nowrap !important;
    overflow: hidden !important;
    text-overflow: ellipsis !important;
    background: #444 !important;
}
#nameinn {
    padding-right: 7px !important;
    padding-top: 22px !important;
    border-right-color: #fff !important;
    height: 39px !important;
}



/* 放送局リスト */
#globalheader #station_menu_eria {
    position: absolute !important;
    top: 82px !important;
    width: 983px !important;
    border-radius: 0 0 3px 3px !important;
    border-width: 1px 0 0 !important;
    padding-left: 15px !important;
    padding-bottom: 1px !important;
}
#globalheader #station_menu #active_first {
    border-radius: 0 !important;
}






/* メイン
---------------------------- */

#stations-top {
    position: relative !important;
    height: 670px !important;
    overflow: hidden !important;
}
#station-prog ul {
    top: 95px !important;
}



/* オンエア曲 */
#stations-middle-right {
    position: absolute !important;
    top: 0 !important;
    left: 0 !important;
    margin-left: 0 !important;
    width: 240px !important;
    height: 432px !important;
    visibility: visible !important;
}
#nowonair-header {
    padding: 7px 10px 7px 14px !important;
    border-radius: 0 !important;
    line-height: 17px !important;
}
.nowonair {
    height: 398px !important;
    border-radius: 0 !important;
}



/* 広告 */
#stations-top-right #cm_ad_list {
    position: absolute;
    top: 437px;
    left: 0;
    height: 197px;
    padding: 15px 18px 0;
    background: #eee;
    border: solid 1px #ccc;
}



/* 番組情報 */
#stations-top-middle {
    position: absolute !important;
    top: 0 !important;
    left: 245px !important;
    width: 483px !important;
    height: 430px !important;
    border: solid 1px #ccc !important;
}
#prog-details {
    width: 463px !important;
    height: 369px !important;
    padding: 10px !important;
    border-bottom: solid 1px #ccc !important;
}
#cm_banner2 .url,
#cm_banner2 .google-btn {
    position: absolute !important;
    top: 391px !important;
    left: 246px !important;
    width: 241px !important;
    height: 20px !important;
    font-size: 0 !important;
    background: #ccc !important;
}
#cm_banner2 .url a,
#cm_banner2 .google-btn a {
    display: block !important;
    position: absolute !important;
    top: 5px !important;
    left: 5px !important;
    width: 231px !important;
    height: 30px !important;
    text-align: center !important;
    line-height: 30px !important;
    border-radius: 5px !important;
    box-shadow: 0 1px 2px #666 !important;
    color: #333 !important;

    background: #ddd !important;
    background: linear-gradient(to bottom, #f5f5f5, #ddd) !important;
}
#cm_banner2 .url a:hover,
#cm_banner2 .google-btn a:hover {
    color: #fff !important;
    box-shadow: 0 1px 0 #666 !important;

    background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#5cc0e8), to(#00a7e9)) !important;
    background: -webkit-linear-gradient(to bottom, #5cc0e8, #00a7e9) !important;
    background: -moz-linear-gradient(to bottom, #5cc0e8, #00a7e9) !important;
    background: -o-linear-gradient(to bottom, #5cc0e8, #00a7e9) !important;
    background: linear-gradient(to bottom, #5cc0e8, #00a7e9) !important;
}
#cm_banner2 .url a:before,
#cm_banner2 .google-btn a:before{
    content: '番組サイトはこちら' !important;
    font-size: 12px !important;
    line-height: 30px !important;
}
#cm_banner2 .google-btn {
    left: 488px !important;
}
#cm_banner2 .google-btn img {
    display: none !important;
}
#cm_banner2 .google-btn a:before {
    content: 'Google カレンダーに追加' !important;
}



/* ツイート (Iframeの使用しているため、調節不可能) */
#stations-top-left {
    position: absolute;
    top: 0;
    left: 735px;
    width: 265px;
    height: 651px !important;
}
#stations-top-left iframe {
    height: 651px !important;
}



/* 番組表 */
#stations-middle-left {
    position: absolute !important;
    top: 437px !important;
    left: 245px !important;
    width: 485px !important;
    height: 214px !important;
}
#stations-middle-left .header_label_rect {
    padding: 7px 10px 7px 14px !important;
    border-radius: 0 !important;
    line-height: 17px !important;
    height: 17px !important;
}
#stations-middle-left .header_label_rect div  {
    margin: 0 !important;
    font-size: 12px !important;
    font-weight: bold !important;
}
#stations-program-container {
    height: 180px !important;
    border-radius: 0 !important;
}
#stations-middle-left .program_table_container .program {height: 180px !important;}
#stations-middle-left .program_table_container .program .program-col {width: 430px !important;}




/* 放送局のツイート or radiko公式のツイート はサイズ上カット */
#stations-middle-center {display: none !important;}
}
