@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

#filter-warning { background-image:url(http://img149.imageshack.us/img149/604/noespg7.png)!important; padding-top: 240px!important; border: 2px solid #293168!important; color: #293168!important; background-color: #77c1d7!important;}

#filter-warning::before {content: "OH NOES!"; display:block; margin-bottom: 5px;}

#filter-warning a {color: #6370c7!important;}

#matureWarning table tbody tr td { background-image:url(http://img149.imageshack.us/img149/604/noespg7.png)!important; padding-top: 240px!important; border: 2px solid #293168!important; color: #293168!important; background-color: #77c1d7!important;}

#matureWarning table tbody tr td a {color: #6370c7!important;}

}
