css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
    .wrapper, .wrapper-narrow, .wrapper-permalink {
    margin: 0 auto;
    min-height: 0;
    padding: 56px 23px 15px;
    position: relative;
    width: 1300px;
}
    .content-main {
    float: right;
    width: 990px;
}
body.three-col .content-main {
    float: right;
    margin: 0 10px;
}
    .fullname {
    color: #666666;
    font-size: 1.2em;
    font-weight: bold;
    line-height: 1.6em;
}
    

.tweet .js-tweet-text {
    color: #666666;
    font-family: georgia;
    font-size: 1.2em;
    font-style: italic;
    line-height: 1.4em;
    white-space: pre-wrap;
}
    body.three-col .wrapper {
    width: 1300px;
}
    .cards-media-container.js-media-container {
    max-width: 600px;
}

}
