css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("korben.info") {
    
    .side, .edito, .edito-footer {
    display: none;
}
    .wrap {
    margin: 0 auto;
    padding: 0;
    width: 1000px;
}
    
.main {
    width: 1000px;
}
    
.main .post-thumbnail {
    width: 1050px !important;;
    height: auto;
}
    
.main .post {
    border-top: 3px solid #eee;
    clear: both;
    padding-top: 30px !important;
}
    
fieldset, img {
    border: 0 none;
    height: auto;
    width: 100%;
}
.post-content {
    width: 100%;
            font-size: 1.2em;
    line-height: 1.6em;
}
.post-share .left-side {
    display: none;
    float: left;
}
#cookie-notice, .main .post-footer, .post-share {
        display: none !important;;
}
script + a > img, .ntContent {
    display: none;
}
.post-content ~ div {
    clear:both;
}

li.comment {
    border-bottom: 1px solid #ddd;
    margin-top: 10px;
}
img.avatar {
    border-radius: 50%;
    margin: 0 10px 0 0;
    width: 32px;
}

.comment-meta.commentmetadata > a {
    color: #999;
    font-size: 12px;
}


}
