css:
@-moz-document domain("aftershock.news")
{

/*27.08.2020*/

/*Левый столбец*/
.aft-sidebar1
  {
  width: 200px !important;
  }

/*Заголовок статьи*/
body .aft-postheader,
body .aft-postheader a:link
  {
  font-size: 22px !important;
  }
  
/*Текст статьи*/
.field.field-name-body *
  {
  font-size: 20px !important;
  line-height: 1.15
  }

/*Текст комментария*/
.field.field-name-comment-body *
  {
  font-size: 20px !important;
  line-height: 1.15
  }

/*Автор*/
a.username
  {
  font-size : 22px !important;
  }

/*Разделитель статьи и комментариев*/
div#comments h2.aft-postheader
  {
  font-size: 22px !important;
  background: #D2DEE4 !important;
  }

/*Жирная кнопка премотки вверх*/
#backtotop
  {
  display: none !important;
  }

}
