css:
@-moz-document domain("forum.fitnessbloggen.no"){

/** Fjerne reklamen, endre bakgrunn og fikse mellomrom**/
.adlink_wallpaper{display:none;}
.ads {display: none;}
html {background: #323232;}
body {background: #323232;}
#toppbanner {height: 50px;}

.home #menu-item-91 a, #XenForo #menu-item-4425 a, .category-styrke #menu-item-5017 a, .category-vektnedgang #menu-item-20009 a, .category-utholdenhet #menu-item-20010 a, .category-helse #menu-item-5016 a, .post-type-archive-oppskrift #menu-item-90 a, .post-type-archive-ovelse #menu-item-89 a, .tax-ovelse-kategori #menu-item-89 a, .tax-oppskrift-type #menu-item-90 a {
	color: #ccc;}

/** Endre farge på hovedmeny **/
#menu-primary {
	background: #202020;
  border-color: #323232;}  

/** Endre farge på topplogo **/
#logo-bar  {
	background: padding-box #667D0D;
        border: 1px solid #C6DB72;}

/** Endre farge på hovedboks **/
#fitnessbloggen  {
	background: #202020;}
  
#content .pageContent,
.sectionMain {
	background: #1a1a1a;
  border-color: #323232;}
  
.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
	background: #1a1a1a;
  border-color: #323232;}
  
.node .nodeLastPost {
	background: #202020;
  border-color: #323232;}
  
/** Endre farge på sticky **/
.discussionListItem.sticky {
	background: #1a1a1a;}

/** Endre farge på editor **/
body, body.mceForceColors { 
background: #000000;
color: #CCCCCC;
font-family: "Arial", sans-serif;
font-size: 11pt;
line-height: 1.4;
}
  
/** Endre farge på forumliste **/
.discussionList .discussionListItem:hover, .discussionListItem:hover .posterAvatar, .discussionListItem:hover .stats,
.discussionListItem .main, .discussionListItem .lastPost{
	background: #202020;}
  
.discussionListItem .posterAvatar, .discussionListItem .stats {
	background: #1a1a1a;}
  
.discussionListItem .stats dl {
	border-color: #323232;
  color: #ccc;}

/** Endre innleggsfarge **/
.message .messageText, .message .signature { 
	color: #ccc;}

/** Endre innleggsbakgrunn **/
.primaryContent {
	background-color: #1a1a1a;}
  
/** Endre farge på meldingsområde **/
.messageList {
	background: #1a1a1a;}
  
/** Endre farge på rammen i meldingsområde **/
.messageList .message {
 border-color: #323232;}

/** Endre farge på quote **/
.bbCodeQuote blockquote {
	background: transparent;}

.secondaryContent {
	background: #202020;}
  
/** Endre farge på ramme i quote **/
.message .likesSummary {
	border-color: #323232;}
  
/** Endre farge på brukerboks **/
.messageUserBlock {
	background: #202020;
  border-color: #323232;}
  
.messageUserBlock div.avatarHolder {
	background: #202020;}
  
.messageUserBlock .extraUserInfo {
	background: #202020;}
  
/** Fjerne avatarramme **/
.avatar img, .avatar .img, .avatarCropper {
  background-color: transparent;
  border-color: #323232;}
  
/** Endre farge på breadcrumbs **/
.breadcrumb {
	background: #202020;
  border-color: #323232;}
  
.breadcrumb .crust a.crumb {
	background: #202020;}
  
.breadcrumb .crust .arrow span,
.breadcrumb .crust .arrow {
	border: none;}
  
/** Søkeboks **/
.formPopup,
#QuickSearch,
.formPopup .controlsWrapper {
	background: #323232;
  color: #ccc;}
  
#QuickSearch .primaryControls input {
	background: #202020;
  border-color: #323232;
  color: #ccc;}

/** Rammer **/
.secondaryContent, 
.sidebar .section .primaryContent h3, 
.sidebar .section .secondaryContent h3, 
.profilePage .mast .section.infoBlock h3,
#content .pageContent,
.hotThreadsListItems .discussionListItem,
.hotThreads .secondaryContent{
          border-color: #323232;}
            
/** Fiks hover **/
.node .nodeInfo.primaryContent:hover, .node .nodeInfo.secondaryContent:hover {background: #202020;}}
