css:
@-moz-document domain("mail.google.com") {
/* UNREAD rows - background colour and opacity and text */
.zE {background-color: #242424 !important; color: #008CFF !important;}

/* Hover Coloring - UNREAD */
table.zt tr.zE:hover { background-color: #0055AB !important; color: white !important; }


/* READ rows - background colour and opacity and text */
.yO {background-color: #0d0d0d !important; color: white !important;}

/* Hover Coloring - READ */
table.zt tr.yO:hover { background-color: #242424 !important; color:#008CFF !important; }


/* SELECTED rows - background colour and opacity*/
.x7 {background-color: #0055AB !important;}

/* Hover Coloring - SELECTED */
table.zt tr.x7:hover { background-color: #242424 !important;}


/* Row dividing line colour and opacity */
.xY {border-color: black !important;}

/* Snippet Text Changes */
.y2 {color: #3d3d3d !important; /*font-family:; font-style:;*/}
}
