css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mangafox.me") {
 #body {
    background: url('http://i.imgur.com/D55bTw6.jpg') no-repeat center;
    background-size:auto 100%;
    font:14px/100% century gothic;
    }
 #menu * {
    font-family:century gothic;
    font-weight:normal;
    background-image:none !important;
    color:white;
    }
 #menu *:hover {
    color:black;
    background-image:none !important;
    }
 #page {
    top:80px;
    }
 #corner {
    width:auto;
    }
 #viewer {
    background: rgba(32, 32, 32, 0.3);
    border:none;
    border-radius: 5px;
    box-shadow: 0 1px 8px #222;
    }
 #image {
    border-radius: 5px;
    box-shadow: 0 0 4px white;
    }
 #series * {
    color: white !important;
    text-shadow: 0 1px 3px #222;
    font-size: 16px !important;
    }
 #chnav,#pgnav,#related *,#tags {
    background: rgba(32, 32, 32, 0.5);
    padding: 3px;
    }
 #copyright {
    background:none;
    }
 #related a {
    background:none;
    }
 #header .widepage {
    width:700px;
    position:relative;
    right:-400px;
    height:30px;
    top:20px;

    }
 #logo {
    background: url('http://i.imgur.com/4zXyBN0.png') no-repeat top center;
    background-size:auto 40px;
    height:40px;
    }
 #header {
    position:fixed;
    width:100%;
    z-index: 1;
    top:-60px;
    transition: ease 0.3s;
    -webkit-transition: ease 0.3s;
    -moz-transition: ease 0.3s;
    background: rgba(32, 32, 32, 0.3);
    }
 #header:hover {
    top:-30px;
    padding:20px 0 0 0;
    box-shadow: 0 1px 3px #222;
    }
 .page {
    margin-top:80px;
    }
 #menu, #comments, #bookmark {
    position:relative;
    left: -800px;
    top:-45px;
    color:white;
    font-size:15px;
    font-weight:normal;
    }
 #comments:hover,#bookmark:hover {
    text-decoration:none;
    text-shadow:0 1px 3px #222;
    font-weight:bold;
    }
 ul#menu li a {
    font-family:century gothic;
    font-weight:normal;
    background: rgba(32, 32, 32, 0.8);
    color:white;
    padding: 6px 10px 0 10px;
    }
 ul#menu li a:hover {
    color:black;
    background: rgba(255,255,255, 1);
    box-shadow: 0 2px 3px #222;
    }
}
