@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vkontakte.ru") {
body 
{
 background: #eaebec top center !important;
 font-family: sans-serif !important
}
#rate{
  display: none !important;
}
#pageLayout
{
  width:66% !important;
}
#sideBar
{
  float:left !important;

  margin:1% 0% 0% 2% !important;
  width:15% !important;
}
#pageHeader 
{
  Background: url(http://funkyimg.com/u2/738/249/4BEb4f7Y9T.png) no-repeat !important;
  width:100% !important;
}

DIV, Select, TD, TEXTAREA, SPAN.ncc, Input, A, #quickquery, B,#endQuote, #startQuote, .actionspro, LI.written, TH, UL.pageList, .actionspro, ul.topNav li, #userProfile, #profileActions, .nc *,.groupslist table,

#td.info 
{
  background: transparent none !important
}
#rateLeft, .voting_result_fg 
{
  border: 1px solid black !important
}

#rateGold 
{
  background: #cccccc !important; 
  border: 1px solid black !important
}
option, #boxWrap 
{
  background: white !important
}
a, h1, h2, #dialog h4, .note strong, h4, .info dt, #header, h3, h5, h6 
{
  color: black !important
}
.mailbox table tr.newRow 
{
  background: #cccccc !important; 
}
#selector 
{
  background: black !important;border: 1px solid black !important 
}
#nameInner, #tagframe2 
{ 
  background-color: white !important;
}
.hm 
{
  background-color: black !important; border: 1px solid black !important 
}
#tagframe2inside3 {
  background-color: black !important; 
}
#tagname 
{
  background: white !important;
  border: 1px solid black !important;}
  .taghandle { background-color: black !important; 
}
#nav li a 
{
  color: black !important; 
}
#myLink, td, small, caption, span, p, b, LI, Div, input, select, option, dd, label, .settingsPanel h4, TextArea 
{
  color: #3b3b3b !important; 
}
#nav li a, .nc *, LI, p, B, #profileActions a, .pageList a, div
{
  border-color: transparent !important;
}
#quickquery, div.actions, #myLink, #content, TextArea,Div.playline, TD, Input, select, TH, H4, H2, #photoactions a, #photoactions .action_link, #photoactions .non_action, .bNav li a, ul.buttonRow a, .header, .flexHeader, #wrapH
{
  border-color: black !important; 
}
#wrapH 
{
  border-bottom: 1px solid black !important
}
#nav li a, .nc *, LI, p, B, #profileActions a, .pageList a, div
{
  border-color:transparent !important;
}
.notes_titles li.app 
{
  background: transparent none !important
}
.rateTabOff a:hover, .rateTabOn a:hover, .rateTabOff a, .rateTabOn a
{
  background-color:white !important; text-decoration:none !important;
}
.FGYellow,.FGGreen,.FGRed,.FGOrange,.FGBlue,.FGMarine,.FGPurple,.FGTeal,.FGWhite 
{
  border: 1px solid black !important;
  background-color: white !important;
}
.FGYellowOn,.FGGreenOn,.FGRedOn,.FGOrangeOn,.FGBlueOn,.FGMarineOn,.FGPurpleOn,.FGTealOn,.FGWhiteOn 
{
  background-color:white !important;
}

}
