css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com")
{



#MessagingNetegoSidebar {
    top: 90px;
}

#MessagingShelfContent {
    border-radius: 0 0 .5em .5em;
}

#blueBar {
transition: opacity .9s ease-out;
background: -webkit-linear-gradient(top, #3B5998, #133783);
box-shadow: 0 0 3px 1px #AAA;
height: 60px;
opacity: .7;
padding-bottom: 0;
padding-left: 4px;
padding-right: 4px;
padding-top: 0;
top: 0;
width: 100%;
}

#blueBar:hover {
    opacity: 1;
}

.back {
transition: opacity .9s ease-out;
box-shadow: 0 0 3px 1px #AAA;
height: 42px;
opacity: .7;
}

.back:hover {
opacity: 1;
}

#leftCol {
background-image: url('http://subtlepatterns.com/images/transp_bg.png');
background-attachment: fixed;
box-shadow: 0 1px 2px 3px #ccc;
margin-left: -15px;
opacity: .6;
overflow: hidden !important;
position: fixed;
width: 180px !important;
}

#leftCol:hover {
opacity: 1;
}



div#contentArea {
width: 786px !important;
}

.fbStoryAttachmentImage {
width: 100% !important;
max-width: 760px !important;
}

.fbNubButton {
opacity: 0.7;
transition: 0.3s;
}
.fbNubButton:hover {opacity: 1.0}

.sidebarMode .fbChatSidebar {
opacity: 0.5;
transition: 0.3s;
}

.sidebarMode:hover .fbChatSidebar:hover {
opacity: 1;
transition: 0.1s;
}

#u_jsonp_4_10, #u_jsonp_4_12, #rightCol {display:none !important;}




@media screen and (max-height: 900px){

#leftCol {
height: 100%;
max-height: 760px !important;
}
#pagelet_navigation {
height: 88% !important;
width: 195px;
overflow: auto !important;
padding-bottom:0 !important;
padding-right: 15px !important;
}
}

