css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("douban.com")
{
    /* fonts */
    *
    {
        font-family: "Microsoft Yahei", Verdana, "Helvetica Neue", "BBAlpha Sans", "S60 Sans", Arial, sans-serif;
    }

    /* background color */
    html, body
    {
        background-color: #76827B !important;
    }

    /* fixed top bar */
    body > .global-nav
    {
        position: fixed !important;
        top: 0 !important;
        width: 100% !important;
        z-index: 99 !important;
    }

    /* header */
    body > .nav
    {
        margin-top: 28px !important;
        margin-bottom: 28px !important;
    }

    /* h2 */
    h2
    {
        border-radius: 3px !important;
        font-weight: bold !important;
        padding: 4px !important;
    }

    /* content area, set default background color */
    #wrapper
    {
        margin-bottom: 28px !important;
        padding: 20px 20px 0 !important;
        border: 6px solid #545652 !important;
        border-radius: 20px !important;
        box-shadow: 0px 5px 5px #000;
        background: none repeat scroll 0% 0% #F6F5F2;
    }
}

@-moz-document
url-prefix("http://www.douban.com"),
url-prefix("https://www.douban.com")
{
    /* anony nav */
    #anony-nav
    {
        background-color: #FFF !important;
        padding: 10px 26px !important;
        margin: 0px auto 30px !important;
        border-radius: 8px !important;
    }

    /* anony footer */
    #anony-events + div.wrapper
    {
        background: none repeat scroll 0% 0% #E8EDF1 !important;
        padding: 0px 26px !important;
        border-radius: 8px !important;
    }

    /* anony content area border-radius */
    #anony-reg ~ div[id^="anony-"] > .wrapper
    {
        padding: 20px 20px 0 !important;
        border: 6px solid #545652 !important;
        border-radius: 20px !important;
        box-shadow: 0px 5px 5px #000;
        background: none repeat scroll 0% 0% #E8EDF1 !important;
    }

    /* anony h2 */
    #anony-reg ~ div[id^="anony-"] > .wrapper h2
    {
        background-color: #C8D5DD !important;
    }

    /* logined content */
    #wrapper
    {
        background: none repeat scroll 0% 0% #EDF4ED !important;
    }

    /* logined h2 */
    #wrapper h2
    {
        background-color: #CDE0CD;
    }

    /* logined highlighter */
    #wrapper p.highlighter
    {
        width: 100% !important;
        height: 100% !important;
    }

    /* logined app list */
    #wrapper a.entrance-link
    {
        background-color: transparent !important;
    }

    /* logined aside header */
    #wrapper .aside .notify-mod
    {
        padding-left: 0 !important;
    }


    /* logined aside bar */
    #wrapper .aside span.more
    {
        line-height: 26px !important;
    }
}

@-moz-document
url-prefix("http://www.douban.com/accounts"),
url-prefix("https://www.douban.com/accounts")
{
    /* content area */
    #wrapper
    {
        margin-top: 48px !important;
    }

    /* border-radius */
    .wrapper
    {
        margin-top: 28px !important;
        margin-bottom: 28px !important;
        padding: 0 20px !important;
        border: 6px solid #545652 !important;
        border-radius: 20px !important;
        box-shadow: 0px 5px 5px #000;
        background: none repeat scroll 0% 0% #EDF4ED !important;
    }
}

@-moz-document domain("book.douban.com")
{
    /* h2 */
    h2
    {
        background-color: #E2E2D1 !important;
    }

    /* h3 */
    h3
    {
        border-radius: 3px !important;
        padding: 4px !important;
        background-color: #E2E2D1 !important;
    }

    /* content area */
    #wrapper
    {
        background: none repeat scroll 0% 0% #F6F6F1 !important;
    }

    /* adjust after logined */
    #wrapper .minibar
    {
        padding: 10px 0px 8px !important;
    }

    /* slide control */
    #wrapper .slide-controls
    {
        margin-top: -45px !important;
        margin-right: 5px !important;
    }

    /* douban book list */
    #wrapper .list-reader b,
    #wrapper ul.app-list
    {
        background-color: transparent !important;
    }
}

@-moz-document domain("movie.douban.com")
{
    /* h2, slide control */
    h2, #mine-tuan
    {
        background-color: #D2DBE1 !important;
    }

    /* content area */
    #wrapper
    {
        background: none repeat scroll 0% 0% #F0F3F5 !important;
    }

    /* cinema search */
    #cinemas-search
    {
        background-color: transparent !important;
        padding: 0 !important;
    }

    /* slide control */
    #wrapper .ui-slide-control
    {
        margin-top: 7px !important;
    }

    /* app list */
    #wrapper a.entrance-link
    {
        background-color: transparent !important;
    }
}

@-moz-document url-prefix("http://movie.douban.com/questions")
{
    /* remove blank area */
    #wrapper span.arr
    {
        display: none !important;
    }
}

@-moz-document domain("music.douban.com")
{
    /* h2 */
    h2
    {
        background-color: #D4DDD1 !important;
    }

    /* content area */
    #wrapper
    {
        background: none repeat scroll 0% 0% #F0F3EF !important;
    }

    /* song chart */
    #song-chart > h2
    {
        padding: 4px 0 4px 4px !important;
    }

    /* song chart more link */
    #song-chart > h2 > div:nth-child(1) > a
    {
        margin-right: 0 !important;
        padding-right: 0 !important;
    }
}

@-moz-document
url-prefix("http://www.douban.com/location"),
url-prefix("http://www.douban.com/event")
{
    /* h2 */
    h2
    {
        margin: 0 !important;
        line-height: 18px !important;
        background-color: #E2DDD4 !important;
    }

    /* content area */
    #wrapper
    {
        background: none repeat scroll 0% 0% #F6F5F2 !important;
    }

    /* h2 */
    #wrapper div.hd
    {
        border-radius: 3px !important;
        padding: 4px !important;
        background-color: #E2DDD4 !important;
    }

    /* span */
    #wrapper div.hd > span.pl.rr
    {
        line-height: 26px !important;
    }

    /* slide control */
    #wrapper div.hosts-mod:nth-child(1) > div:nth-child(1) > span
    {
        margin-top: 4px !important;
    }

    /* slide control */
    #wrapper div.hosts-mod:nth-child(1) > div:nth-child(1) > ul
    {
        margin-top: 9px !important;
    }

    /* tabs */
    #wrapper div.tabs
    {
        line-height: 45px !important;
    }

    /* slide control */
    #wrapper .ui-slide-control
    {
        line-height: 27px !important;
    }

    /* slide control */
    #wrapper .ui-slide-control > a[class^="btn-"]
    {
        background-color: transparent !important;
    }

    /* app list */
    #wrapper a.entrance-link
    {
        background-color: transparent !important;
    }

    /* area dropdown list */
    #wrapper .selected
    {
        background-color: transparent !important;
    }
}

@-moz-document url-prefix("http://www.douban.com/group")
{
    /* h2 */
    h2
    {
        background-color: #D0E2D9 !important;
    }

    /* content area */
    #wrapper
    {
        background: none repeat scroll 0% 0% #F0F6F3 !important;
    }

    /* aside bar more link */
    #wrapper .aside span.more
    {
        margin-top: 2px !important;
    }

    /* app list */
    #wrapper .aside .mod-app-entrance
    {
        background-color: transparent !important;
    }

    /* popup dialog */
    .fixed-fields
    {
        background-color: transparent !important;
        padding-top: 0 !important;
        margin-top: 38px !important;
    }
}

@-moz-document domain("read.douban.com")
{
    /* border-radius and background color */
    .wrapper .main
    {
        padding: 20px 30px 0 20px !important;
        border: 6px solid #545652 !important;
        border-radius: 20px !important;
        box-shadow: 0px 5px 5px #000;
        background: none repeat scroll 0% 0% #F6F6F1 !important;
    }

    /* slide control */
    .aside-slide-controls
    {
        background-color: transparent !important;
    }
}

@-moz-document url-prefix("http://read.douban.com/reader")
{
    /* html background */
    html
    {
        background: url("data:image/png;base64,") repeat scroll 0% 0% #E0DCD5 !important;
    }

    /* body background color */
    body
    {
        background-color: transparent !important;
        padding-bottom: 0 !important;
    }

    /* h2 */
    h2
    {
        padding: 0 !important;
    }
}

@-moz-document domain("douban.fm")
{
    /* TODO */

    /* fonts */
    *
    {
        font-family: "Microsoft Yahei", Verdana, "Helvetica Neue", "BBAlpha Sans", "S60 Sans", Arial, sans-serif;
    }

    /* border-radius */
    body > #main
    {
        margin-top: 28px !important;
        padding: 0 20px !important;
        border: 6px solid #545652 !important;
        border-radius: 20px !important;
        box-shadow: 0px 5px 5px #000;
        background: none repeat scroll 0% 0% #FFF;
    }

    /* hide footer */
    body > #fm-app-footer > .copyright
    {
        display: none !important;
    }
}

@-moz-document
url("http://douban.fm/app"),
url("https://douban.fm/app")
{
    /* background color */
    html, body
    {
        background-color: #76827B !important;
    }

    /* header */
    #logo
    {
        margin-top: 28px !important;
    }
}

@-moz-document domain("dongxi.douban.com")
{
    /* content area */
    #wrapper
    {
        background: none repeat scroll 0% 0% #F7F8ED !important;
    }

    /* dongxi logo */
    .nav-logo > a
    {
        height: 61px !important;
    }
}

@-moz-document url-prefix("http://www.douban.com/mobile")
{
    /* background color */
    #wrapper,
    #wrapper div.show-more-link-wrap
    {
        background: none repeat scroll 0% 0% #EDF4ED !important;
    }

    /* app preview */
    #wrapper div.app-preview
    {
        background-color: transparent !important;
    }
}

@-moz-document url-prefix("http://www.douban.com/share/service")
{
    /* TODO */
}

/* 9.douban */
@-moz-document domain("9.douban.com")
{
    /* body background color */
    body
    {
        background-color: #4A5875 !important;
    }

    /* header */
    body > .header
    {
        margin-bottom: 28px !important;
    }

    /* seperator */
    body > .bold_line
    {
        display: none !important;
    }

    /* border-radius and background color */
    body > .wapper.main
    {
        padding: 20px 20px 0 !important;
        border: 6px solid #545652 !important;
        border-radius: 20px !important;
        box-shadow: 0px 5px 5px #000;
        background: none repeat scroll 0% 0% #F6F6F1 !important;
    }

    /* footer links */
    .fnav a
    {
        color: #999 !important;
    }
}

@-moz-document url-prefix("http://moment.douban.com/post/")
{
    /* content area */
    .post-container
    {
        margin-bottom: 28px !important;
        padding: 20px 20px 0 !important;
        border: 6px solid #545652 !important;
        border-radius: 20px !important;
        box-shadow: 0px 5px 5px #000;
        background: none repeat scroll 0% 0% #F6F5F2 !important;
    }
    
    .bs-header, #sideNav
    {
        display: none !important;
    }
    
    .container.bs-docs-container
    {
        margin-top: 12px !important;
        max-width: 80% !important;
    }
}
