css:
@-moz-document url-prefix("http://www.nicovideo.jp/my") {
.log-community-live-broadcast, .nicorepoTabChild .child li:nth-of-type(2), .videoTab, .stampTab, .nicoruTab, .buyhistoryTab, .translationTab, .footerBanner, .topic_wrap {
    display: none !important;
}
.sidebar {
    height: 0px !important;
}
.timeline .log.log-user-mylist-add, .timeline .log.log-community-live-reserve, .timeline .log.log-community-action-level, .timeline .log.log-user-live-reserve {
    border-width: 3px 3px 3px 34px !important;
    border-color: #BBA4A0 !important;
    box-shadow: -3px 0px 2px rgba(0, 0, 0, 0.2) !important;
}
.timeline .log.log-community-live-reserve, .timeline .log.log-community-action-level, .timeline .log.log-user-live-reserve {
    border-color: #C2DBFF !important;
}
.timeline .log.log-user-mylist-add .log-content, .timeline .log.log-community-live-reserve .log-content, .timeline .log.log-community-action-level .log-content, .timeline .log.log-user-live-reserve .log-content {
    margin-right: 114px !important;
    min-height: 70px !important;
}
.timeline .log.log-user-mylist-add .log-author, .timeline .log.log-community-live-reserve .log-author, .timeline .log.log-community-action-level .log-author, .timeline .log.log-user-live-reserve .log-author {
    top: 0px !important;
}
.wrapper .content {
    min-height: 1000px;
}
.videoTab {
    display: none !important;
}
/* スタンプ帳 */

.stampTab {
    display: none !important;
}
/* ニコる */

.nicoruTab {
    display: none !important;
}
/* 購入履歴 */

.buyhistoryTab {
    display: none !important;
}
/* 翻訳履歴 */

.translationTab {
    display: none !important;
}
/*for 自動読み込み */

.toggleUpload {
    display: inline-block;
    margin-left: 8px;
    font-weight: normal;
    font-size: 77%;
    color: rgb(0, 0, 0);
    top: 5px;
    left: 397px;
    height: 28px;
}
button.toggleUpload:nth-child(21) {
    display: none !important;
}
#nicorepo .timeline > .log:hover:not(.log-user-video-upload):not(.log-user-seiga-image-upload):not(.log-user-register-chblog) {
    overflow: visible !important;
    transition: max-height 0s ease-in-out 0s;
}
.nicorepo .log.log-user-video-upload .video, .nicorepo .log.log-user-seiga-image-upload .seiga_image {
    width: 104px !important;
}
#personalFrameArea {
    display: none;
}
.articleBody .nicorepo .nicorepo-page .log.log-community-video-upload {
    border-color: #FFCAA1 !important;
}
}

@-moz-document url-prefix("http://www.nicovideo.jp/watch-disable") {
#bottomContentTabContainer, #playlist, .niconikoukoku, #videoExplorerExpand, #footer, #appliPanel, .area-JP .panel_ads_shown #playerTabContainer.has_panel_ads .playerTabAds, body.ja-jp #textMarquee {
    display: none !important;
}
#videoHeader #videoTagContainer .tagInner #videoHeaderTagList li .tagControlContainer .dic span.disable, #videoHeader #videoTagContainer .tagInner #videoHeaderTagList li .tagControlContainer .dic span.enable {
    background-size: inherit !important;
}
body #siteHeader #siteHeaderInner .siteHeaderMenuList {
    float: right !important;
}
body {
    background: #F4F4F4 !important;
}
.player-panel-tabs .player-tab-content {
    bottom: 28px !important;
    padding: 5px !important;
}
}

@-moz-document url-prefix("http://www.nicovideo.jp/watch") {
#playerContainerWrapper {
    padding: 10px 0;
}
}

@-moz-document url-prefix("http://live2.nicovideo.jp/watch"), url-prefix("http://live.nicovideo.jp/watch") {
body {
    background-image: none !important;
    background-color: #f4f4f4 !important;
}
}

@-moz-document url-prefix("https://twitter.com") {
.module.trends, .module.wtf-module.js-wtf-module.has-content, .Footer.module {
    display: none !important;
}
.permalink-tweet .AdaptiveMedia.is-square {
    max-height: 5000px;
}
button.btn.btn-neutral-positive.full-width.btn-bg-positive.btn-options-tray-space {
    color: #fff;
}
}

@-moz-document url-prefix("https://www.google.co.jp/search") {
.sfbgg {
    box-shadow: none;
}
}

@-moz-document url-prefix("http://news.yahoo.co.jp") {
#main > noscript {
    display: none !important;
}
}

@-moz-document domain("tenki.jp") {
.forecast_point_other_link_img, .forecast_point_other_link {
    display: none !important;
}
}

@-moz-document domain("nicovideo.jp") {
/* #body_header #siteHeader #siteHeaderInner {
    width: 100% !important;
} */
}

@-moz-document domain("fe-siken.com") {
.ad, .amazon300 {
    display: none !important;
}
}

@-moz-document domain("fc2.com") {
#fc2_bottom_bnr {
    display: none !important;
}
}

@-moz-document url-prefix("https://cpanel.hostinger.jp/hosting/") {
.container-fluid > div:nth-child(3) {
    display: none !important;
}
}

@-moz-document domain("soundcloud.com") {
.l-fixed-top-one-column>.l-top {
    background: rgba(255, 255, 255, .8) !important;
}
}

@-moz-document domain("steamcommunity.com"), domain("steampowered.com") {
progress#es_progress {
    display: none !important
}
}

@-moz-document url-prefix("http://www.dmm.com/netgame/social/-/gadgets/=/app_id=") {
.page-appdetial .area-pickupgame {
    display: none !important;
}
}

@-moz-document domain("pastebin.com") {
#abrpm, #super_frame > div:nth-child(4) {
    display: none !important;
}
}

@-moz-document domain("srytk.com") {
.price.cheapestPrice {
    background: #428BCA !important;
    color: #fff !important;
}
}

@-moz-document domain("2game.info") {
body {
    background-image: none;
}
}

@-moz-document domain("play.spotify.com") {
#now-playing-widgets {
    display: none;
}
}

@-moz-document domain("steamcommunity.com"), domain("steampowered.com") {
div#global_header {
    height: 75px;
}
div#global_header .content {
    height: 0;
}
div#global_header div.logo {
    padding-top: 20px;
}
}
