css:
@-moz-document url-prefix("https://www.facebook.com/") {

#facebook #contentCol div.hidden_elem {
  	display: block !important;  
}

#rightCol {
	display: none; 
}

.hasLeftCol #leftCol {
 	display: none; 
}
  
.hasLeftCol.home #contentCol.homeWiderContent {
	margin-left: 0;
    padding: 10px;
}

body.home .fbChatSidebar {
	display: none;  
}
      
body.home #globalContainer {
	width: 100%;        
}
      
body.home #blueBarHolder #blueBar #pageHead {
	margin: 0 5px 0 5px;
    width: auto;
}
      
.hasLeftCol.home.home.home .hasRightCol div#contentArea {
	width: auto;        
}


#pagelet_composer div.mbm {
	min-height: 100px;
  	height: auto;
}

div.mbm {
	width: 32.0%;
	margin: 0 5px 5px 0;
	display: inline-block;
	float: left;
	vertical-align: top;
	height: 350px;
	overflow: hidden;
    padding: 0;
}

div.mbm:hover {
	overflow: visible;
    z-index: 3;
    background: white;
}
  
div.mbs div, div.mtm div div div {
	font-family: Helvetica, Arial, Verdana;
    font-size: 13px;
}

div.userContentWrapper {
	padding: 10px;
	border: 2px solid white;
    border-radius: 4px;
}
  
div.mbm:hover div.userContentWrapper {
	background: white;    
	border: 2px solid #CCC;
    z-index: 4;
    min-height: 94%;
}
  
div.mbl {
	clear: both;
}

div.uiUfi.UFIContainer {
	display: none; 
}

div.userContentWrapper:hover div.uiUfi.UFIContainer {
	display: block;
  	margin: 0;
}

div.mtm > div {
 	text-align: center; 
}

div.mtm > div > a {
    float: none;
	display: inline;
}

div.mtm > div > a > div {
	overflow: visible;
    width: 100%;
    margin: auto;
  	display: inline;
}

div.mvm > div > div > div {
	display: inline; 
}
  
div.mtm img.img {
	width: 50%;
    height: auto;
  	margin: auto;
    position: relative;
}

div.userContentWrapper:hover div.mtm img.img {
 	zoom: 1.0; 
	width: 100%;
}

img.fbStoryAttachmentImage {
 	zoom: 0.5; 
}

#facebook .uiScaledImageContainer {
	display: inline; 
}

#facebook .uiScaledImageContainer img {
    min-height: 0;
    height: auto;
}
}
