@-moz-document domain("3dsplaza.com") {


/*Coded by mega*/

body{
background-color:#259AD6;
}

a:link {
color:#000000;
font-weight:bold;
}

a:hover{
  color:#000080;
  font-weight:bold;
}

a:visited{
 color:#000000;
 font-weight:bold;
}

img#header{
 
content:url(https://i.imgur.com/KgUGwnU.png);
}

h1{
 color:red;
}

div#content.content{
background-image: -webkit-gradient(
	linear,
	left top,
	left bottom,
	color-stop(0, #56ABED),
	color-stop(1, #CCCCCC)
);
background-image: -o-linear-gradient(bottom, #56ABED 0%, #CCCCCC 100%);
background-image: -moz-linear-gradient(bottom, #56ABED 0%, #CCCCCC 100%);
background-image: -webkit-linear-gradient(bottom, #56ABED 0%, #CCCCCC 100%);
background-image: -ms-linear-gradient(bottom, #56ABED 0%, #CCCCCC 100%);
background-image: linear-gradient(to bottom, #56ABED 0%, #CCCCCC 100%);
    
}

td{
background-image: -webkit-gradient(
	linear,
	left top,
	left bottom,
	color-stop(0, #56ABED),
	color-stop(1, #CCCCCC)
);
background-image: -o-linear-gradient(bottom, #56ABED 0%, #CCCCCC 100%);
background-image: -moz-linear-gradient(bottom, #56ABED 0%, #CCCCCC 100%);
background-image: -webkit-linear-gradient(bottom, #56ABED 0%, #CCCCCC 100%);
background-image: -ms-linear-gradient(bottom, #56ABED 0%, #CCCCCC 100%);
background-image: linear-gradient(to bottom, #56ABED 0%, #CCCCCC 100%);
}

