css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.facebook.com/") {
    *[dir="rtl"], textarea  {
       font-family: Tahoma;
       font-size: 11.5px;
       line-height: 18px;
    }
    
    body, #contentCol {
        background-color: #efefef !important;
    }
    
    a:after {
        border: none !important;
    }
    
    a .img {
        border-radius: 3px !important;
    }
    
    textarea  {
       font-family: Tahoma !important;
       font-size: 12px !important;
       line-height: 18px !important;
    }
    
    .userContent[dir="rtl"] {
       font-size: 12px;
    }
    
    div#rightCol {
        width: 170px !important;
        opacity: 0.8;
        transition: all 0.3s ease-in-out;
        margin-right: -50px !important;
        right: 50px !important;
        position: relative;
    }
    
    div#rightCol:hover {
        opacity: 1;
    }
    
    div#contentArea {
        width: 596px !important;
    }
    
    .mvm {
        width: 574px !important;
        height: auto;
    }
    
    .mtm a[href^="http://"] div img {
        width: 100% !important;
        height: auto !important;
    }
    
    .mtm a div, .mtm a div img {
        width: 570px !important;
        height: auto
    }
    
    .swfObject embed {
        width: 570px !important
    }
    
    .mtm a div img {
        
    }
    
    #leftCol {
        opacity: 0.75;
        transition: all 0.3s ease-in-out;
        margin-left: -25px;
        width: 185px !important;
    }
    
    #leftCol:hover {
        opacity: 1;
    }
    
    .fbTimelineScrubber {
        margin-left: 50px !important;
        background: #efefef !important;
    }
    
    .messages .direction_rtl {
        font-family: tahoma !important;
        font-size: 11px !important;
    }
    
    .pagesTimelineButtonPagelet {
        margin-left: 50px !important;
    }
    
    .cardRightCol {
        width: 200px !important;
        margin-left: -10px !important
    }
    
    #mainContainer .groupLitestandClassicJumpLayout div#headerArea {
        width: 833px !important;
    }
    
    #event_related_events {
        display: none !important;
    }
    
    .fbEventHeader {
        width: 803px !important;
    }
    
    #fbProfileCover .coverBorder {
        width: 100% !important;
    }
}
