/* ::::::::::::::::::::::::::::::::::::::::::::Findbar::::::::::::::::::::::::::::::::::::::::::::::::::*/

#FindToolbar > *,
.findbar-container > * 
{
display:-moz-box !important;
}

#FindToolbar 
{ 
background: rgb(75, 75, 75) !important;
border: 0px !important;
}

#FindToolbar .findbar-find-fast, #FindToolbar .checkbox-label, #FindToolbar .toolbarbutton-text
{
color: #fff !important;
}

/*:::::::::::::::::::::::::::::::::::::::::::::::Tabs:::::::::::::::::::::::::::::::::::::::::::::::::::*/

.tabbrowser-tabs
{
-moz-appearance: none !important;
background: #135E98 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAeCAIAAABi9%2BOQAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAB3RJTUUH2AcZAA0oLZMmUAAAAAd0RVh0QXV0aG9yAKmuzEgAAAAMdEVYdERlc2NyaXB0aW9uABMJISMAAAAKdEVYdENvcHlyaWdodACsD8w6AAAADnRFWHRDcmVhdGlvbiB0aW1lADX3DwkAAAAJdEVYdFNvZnR3YXJlAF1w%2FzoAAAALdEVYdERpc2NsYWltZXIAt8C0jwAAAAh0RVh0V2FybmluZwDAG%2BaHAAAAB3RFWHRTb3VyY2UA9f%2BD6wAAAAh0RVh0Q29tbWVudAD2zJa%2FAAAABnRFWHRUaXRsZQCo7tInAAAAG0lEQVQImWMQVjFgYmBiYWJgYsZBU1%2FuPxMzAGSdAkLBLrwyAAAAAElFTkSuQmCC") repeat-x top !important;
}

.tabbrowser-tab[selected="true"]
{
-moz-appearance: none !important;
border: 0 !important;
}

tab .tab-progress{ 
        -moz-appearance: progressbar !important;
	margin:0px 0px 0px -18px !important;
	opacity:.5 !important; 
}

.tabbrowser-tab:not([selected="true"])
{
background: #000000 
url("data:image/png;base64,") repeat-x center !important;
}

.tabbrowser-tab:not([selected="true"]):hover
{
background: #135E98 url("data:image/png;base64,") repeat-x center !important;
}

.tabbrowser-tab[selected="true"]
{
min-width: 150px !important;
background: #000000 url("data:image/png;base64,") repeat-x center !important;
}

.tabs-bottom 
{
background: #000000 url("data:image/png;base64,") repeat-x bottom !important;
}

.tabbrowser-tab .tab-text 
{
color: white !important;
}

/* ::::: Status Bar Style ::::: */

statusbarpanel
{
-moz-appearance  : none !important;
border: none !important;
background: #0f0f14 url("data:image/png;base64,") repeat-x center !important;
color: white !important;

}

/* :::::::::::::::::::::::::::::::::::::::::: Vista Theme ::::::::::::::::::::::::::::::::::::::::::::: */

/*||||||||||||||||||||Global Styles Toolbox & Toolbar|||||||||||||||||||||||*/

#navigator-toolbox,
#navigator-toolbox #toolbar-menubar,
#navigator-toolbox #nav-bar,
#navigator-toolbox #PersonalToolbar
{
-moz-appearance: none !important;
border: 0 !important;
}

#navigator-toolbox,
#navigator-toolbox .toolbarbutton-text,
#navigator-toolbox .menubar-text
{
color: white !important;
}

/*||||||||||||||||||||||||||||||Toolbox|||||||||||||||||||||||||||||||||||||*/

#navigator-toolbox 
{
-moz-appearance: none !important;
background-image: url("data:image/png;base64,") !important;
color: white !important;
}

/*||||||||||||||||||||||||||Navigation Toolbar||||||||||||||||||||||||||||||*/

/* before BG 22697E*/
#navigator-toolbox #nav-bar 
{
-moz-appearance: none !important;
background: #FFFFFF 
url("data:image/png;base64,") repeat-x center !important;
}

/*|||||||||||||||||||||||Personal/Bookmarks Toolbar|||||||||||||||||||||||||*/

#navigator-toolbox #PersonalToolbar 
{
-moz-appearance: none !important;
background: #22697E url("data:image/png;base64,") repeat-x bottom !important;
}

/*||||||||||||||||||Sidebar & Organizer - Header & Panel||||||||||||||||||||*/


#placesToolbar
{
-moz-appearance: none !important;
background: #000000 url("data:image/png;base64,") repeat-x center !important;
border: 0 !important;
}

#organizerScopeBar,
sidebarheader,
#placesToolbar .menubar-text,
#organizerScopeBar .toolbarbutton-text
{
color: white !important
}

sidebarheader,
#organizerScopeBar
{
-moz-appearance: none !important;
background: #22697E url("data:image/png;base64,") repeat-x center !important;
border: none !important;
}

#placesView tree,
#infoPane,
#bookmarks-view,
#historyTree 
{
background-color: rgb(238,243,250) !important;
}

#splitter,
#contentSplitter,
#sidebar-splitter 
{
width: 2px !important;
height: 2px !important;
min-width: 1px !important;
min-height: 1px !important;
background-color: rgb(193,209,227) !important;
border: none !important;
}

#contentSplitter grippy,
#splitter grippy 
{
visibility: hidden !important;
}

/*|||||||||||||||||||||||||URL Bar & Search Bar|||||||||||||||||||||||||||||*/

#searchFilter,
#urlbar,
.searchbar-textbox 
{
opacity: 0.8 !important;
}

.searchbar-textbox:hover,
#urlbar:hover,
#searchFilter:hover
{
opacity: 0.9 !important;
}

.searchbar-textbox[focused="true"],
#urlbar[focused="true"],
#searchFilter[focused="true"] 
{
opacity: 1 !important;
}
